--醉生梦死酒。喝后可以忘掉一种武功
function newThing_0(id)
    if JY.Status ==GAME_STATUS_WARMAP then
	    return 0;
	end

    Cls();
    if DrawStrBoxYesNo(-1,-1,"喝后会忘记武功，但损害生命，是否继续?",C_WHITE,CC.DefaultFontSize,1) == false then
