-- 菜单系统

function GetTextSize(text, fontSize)
	return fontSize / 2.0 * #text, fontSize
end


function GetVerticalMenuRect(menu, x, y)
	local cx, cy = GetVerticalMenuSize(menu)
	return {x = x, y = y, cx = cx, cy = cy}
end


function GetVerticalMenuItemRects(menu, x, y)
	local rects = {}
	if menu.hasBorder then
		x = x + CC.MenuBorderPixel
		y = y + CC.MenuBorderPixel
	end
	for i = 1, #menu do
		local cx, cy = GetTextSize(menu[i].text, menu.fontSize)
		rects[i] = {x = x, y = y, cx = cx, cy = cy}
		y = y + cy + CC.LINE_SPACING
	end
	return rects
end


function GetVerticalMenuSize(menu)
	local numItems = #menu
	local cy = (numItems - 1) * CC.LINE_SPACING + numItems * menu.fontSize
	local cx = 0
	for i = 1, numItems do
		local tcx, _ = GetTextSize(menu[i].text, menu.fontSize)
		if tcx > cx then
			cx = tcx
		end
	end
	if menu.hasBorder then
		cx = cx + 2 * CC.MenuBorderPixel
		cy = cy + 2 * CC.MenuBorderPixel
	end
	return cx, cy
end

--通用菜单函数
-- menuItem 表，每项保存一个子表，内容为一个菜单项的定义
--          菜单项定义为  {   ItemName,     菜单项名称字符串
--                          ItemFunction, 菜单调用函数，如果没有则为nil
--                          Visible       是否可见  0 不可见 1 可见, 2 可见，作为当前选择项。只能有一个为2，
--                                        多了则只取第一个为2的，没有则第一个菜单项为当前选择项。
--                                        在只显示部分菜单的情况下此值无效。
--                                        此值目前只用于是否菜单缺省显示否的情况
--                       }
--          菜单调用函数说明：         itemfunction(newmenu,id)
--
--       返回值
--              0 正常返回，继续菜单循环 1 调用函数要求退出菜单，不进行菜单循环
--
-- numItem      总菜单项个数
-- numShow      显示菜单项目，如果总菜单项很多，一屏显示不下，则可以定义此值
--                =0表示显示全部菜单项

-- (x1,y1),(x2,y2)  菜单区域的左上角和右下角坐标，如果x2,y2=0,则根据字符串长度和显示菜单项自动计算x2,y2
-- isBox        是否绘制边框，0 不绘制，1 绘制。若绘制，则按照(x1,y1,x2,y2)的矩形绘制白色方框，并使方框内背景变暗
-- isEsc        Esc键是否起作用 0 不起作用，1起作用
-- Size         菜单项字体大小
-- color        正常菜单项颜色，均为RGB
-- selectColor  选中菜单项颜色,
--
-- 返回值  0 Esc返回
--         >0 选中的菜单项(1表示第一项)
--         <0 选中的菜单项，调用函数要求退出父菜单，这个用于退出多层菜单

function ShowVerticalMenu(menuItem,numItem,numShow,x1,y1,x2,y2,isBox,isEsc,fontSize,color,selectColor)     --通用菜单函数
	local w=0
	local h=0   --边框的宽高
	local i=0
	local num=0     --实际的显示菜单项
	local newNumItem=0  --能够显示的总菜单项数

	--lib.GetKey()
	ClearKeyBuffer()

	local newMenu={}   -- 定义新的数组，以保存所有能显示的菜单项
	--计算能够显示的总菜单项数
	for i=1, numItem do
		if menuItem[i][3]>0 then
			newNumItem=newNumItem+1
			newMenu[newNumItem]={menuItem[i][1],menuItem[i][2],menuItem[i][3],i}   --新数组多了[4],保存和原数组的对应
		end
	end

	--计算实际显示的菜单项数
	if numShow==0 or numShow > newNumItem then
		num=newNumItem
	else
		num=numShow
	end

	--计算边框实际宽高
	local maxlength=0
	if x2==0 and y2==0 then
		for i=1,newNumItem do
			if string.len(newMenu[i][1])>maxlength then
				maxlength=string.len(newMenu[i][1])
			end
		end
		w=fontSize*maxlength/2+2*CC.MenuBorderPixel        --按照半个汉字计算宽度，一边留4个象素
		-- BUG FIXED, 应该是 2 * CC.MenuBorderPixel
		h=(fontSize+CC.LINE_SPACING)*num + 2*CC.MenuBorderPixel            --字之间留4个象素，上面再留4个象素
	else
		w=x2-x1
		h=y2-y1
	end

	local start=1             
	local current =1          --当前选择项
	for i=1,newNumItem do
		if newMenu[i][3]==2 then
			current=i
			break
		end
	end
	if numShow~=0 then
		current=1
	end

	local keyPress =-1
	local returnValue =0

	--	if isBox==1 then
	--		DrawDialogBorder(x1,y1,x1+w,y1+h,C_WHITE)
	--	end

	while true do
		Cls(x1, y1, x1+w, y1+h)  -- fix: <ZG> 修正字体显示越来越粗显示的BUG
		if isBox == 1 then
			DrawDialogBorder(x1, y1, x1+w, y1+h, C_WHITE)
		end

		for i=start,start+num-1 do
			local drawColor=color           --设置不同的绘制颜色
			if i==current then
				drawColor=selectColor
			end
			DrawText(x1+CC.MenuBorderPixel,y1+CC.MenuBorderPixel+(i-start)*(fontSize+CC.LINE_SPACING),
			newMenu[i][1],drawColor,fontSize)
		end
		UpdateScreen()
		keyPress=WaitKey()
		lib.Delay(100)
		if keyPress==VK_ESCAPE then                  --Esc 退出
			if isEsc==1 then
				break
			end
		elseif keyPress==VK_DOWN then                --Down
			current = current +1
			if current > (start + num-1) then
				start=start+1
			end
			if current > newNumItem then
				start=1
				current =1
			end
		elseif keyPress==VK_UP then                  --Up
			current = current -1
			if current < start then
				start=start-1
			end
			if current < 1 then
				current = newNumItem
				start =current-num+1
			end
		elseif (keyPress==VK_SPACE) or (keyPress==VK_RETURN)  then
			if newMenu[current][2]==nil then
				returnValue=newMenu[current][4]
				lib.Log(string.format("no func: current = %s, ret = %d", current, returnValue))
				break
			else
				local r=newMenu[current][2](newMenu,current)               --调用菜单函数
				if r==1 then
					returnValue= -newMenu[current][4]
					lib.Log(string.format("func: current = %s, ret = %d", current, returnValue))
					break
				else
					Cls(x1,y1,x1+w,y1+h)
					if isBox==1 then
						DrawDialogBorder(x1,y1,x1+w,y1+h,C_WHITE)
					end
				end
			end
		end
	end
	Cls(x1,y1,x1+w+1,y1+h+1,0,1)
	return returnValue
end


function ShowVerticalMenu3(menu, x, y)     --通用菜单函数
	local numItems = #menu
	local ret = 0
	local activeItem = menu.defaultItem or 1
	local menu_item_rects = GetVerticalMenuItemRects(menu, x, y)
	local menuRect = GetVerticalMenuRect(menu, x, y)
	while true do
		local r = menuRect
		Cls(r.x, r.y, r.x+r.cx, r.y+r.cy)
		if menu.hasBorder then
			DrawDialogBorder(r.x, r.y, r.x + r.cx, r.y + r.cy, menu.borderColor) 
		end
		for i = 1, numItems do
			local color = 0
			if activeItem == i then
				color = menu.activeItemColor
			elseif menu[i].disabled then
				color = C_GRAY
			else
				color = menu.fontColor
			end
			local r = menu_item_rects
			DrawText(r[i].x, r[i].y, menu[i].text, color, menu.fontSize)
		end
		UpdateScreen()
		local key = WaitKey()
		lib.Delay(100)
		if key == VK_DOWN then
			activeItem = activeItem + 1
			if activeItem > numItems then
				activeItem = 1
			end
		elseif key == VK_UP then
			activeItem = activeItem - 1
			if activeItem < 1 then
				activeItem = numItems
			end
		elseif key == VK_RETURN or key == VK_SPACE and not menu[activeItem].disabled then
			if menu[activeItem].func then
				local r = menu[activeItem].func(menu, activeItem)
				if r == 1 then
					ret = -activeItem
					break
				else
					if menu.hasBorder then
						local r = menuRect
						DrawDialogBorder(r.x, r.y, r.x+r.cx, r.y+r.cy, menu.borderColor)
					end
				end
			else
				ret = activeItem
				break
			end
		elseif key == VK_ESCAPE and menu.canExit then
			break
		end
	end
	Cls(menuRect.x, menuRect.y, menuRect.x+menuRect.cx, menuRect.y+menuRect.cy)
	return ret
end


--横向显示菜单，参数和ShowVerticalMenu一样
function ShowHorizontalMenu(menuItem,numItem,numShow,x1,y1,x2,y2,isBox,isEsc,fontSize,color,selectColor)     --通用菜单函数
	local w=0
	local h=0   --边框的宽高
	local i=0
	local num=0     --实际的显示菜单项
	local newNumItem=0  --能够显示的总菜单项数

	--lib.GetKey()
	ClearKeyBuffer()
	local newMenu={}   -- 定义新的数组，以保存所有能显示的菜单项

	--计算能够显示的总菜单项数
	for i=1,numItem do
		if menuItem[i][3]>0 then
			newNumItem=newNumItem+1
			newMenu[newNumItem]={menuItem[i][1],menuItem[i][2],menuItem[i][3],i}   --新数组多了[4],保存和原数组的对应
		end
	end

	--计算实际显示的菜单项数
	if numShow==0 or numShow > newNumItem then
		num=newNumItem
	else
		num=numShow
	end

	--计算边框实际宽高
	local maxlength=0
	if x2==0 and y2==0 then
		for i=1,newNumItem do
			if string.len(newMenu[i][1])>maxlength then
				maxlength=string.len(newMenu[i][1])
			end
		end
		w=(fontSize*maxlength/2+CC.LINE_SPACING)*num
		h=fontSize
		if isBox == 1 then
			w = w + 2 * CC.MenuBorderPixel
			h = h + 2 * CC.MenuBorderPixel
		end
	else
		w=x2-x1
		h=y2-y1
	end

	local start=1             --显示的第一项

	local current =1          --当前选择项
	for i=1,newNumItem do
		if newMenu[i][3]==2 then
			current=i
			break
		end
	end
	if numShow~=0 then
		current=1
	end

	local keyPress =-1
	local returnValue =0
	if isBox==1 then
		DrawDialogBorder(x1,y1,x1+w,y1+h,C_WHITE)
	end
	while true do
		if false then
			if numShow ~=0 then
				Cls(x1,y1,x1+w,y1+h)
				if isBox==1 then
					DrawDialogBorder(x1,y1,x1+w,y1+h,C_WHITE)
				end
			end
		end

		Cls(x1, y1, x1+w, y1+h)
		if isBox == 1 then
			DrawDialogBorder(x1, y1, x1+w, y1+h, C_WHITE)
		end

		for i=start,start+num-1 do
			local drawColor=color           --设置不同的绘制颜色
			if i==current then
				drawColor=selectColor
			end
			DrawText(x1+CC.MenuBorderPixel+(i-start)*(fontSize*maxlength/2+CC.LINE_SPACING),
			y1+CC.MenuBorderPixel,newMenu[i][1],drawColor,fontSize)
		end
		UpdateScreen()
		keyPress=WaitKey()
		lib.Delay(100)

		if keyPress==VK_ESCAPE then                  --Esc 退出
			if isEsc==1 then
				break
			end
		elseif keyPress==VK_RIGHT then                --Down
			current = current +1
			if current > (start + num-1) then
				start=start+1
			end
			if current > newNumItem then
				start=1
				current =1
			end
		elseif keyPress==VK_LEFT then                  --Up
			current = current -1
			if current < start then
				start=start-1
			end
			if current < 1 then
				current = newNumItem
				start =current-num+1
			end
		elseif   (keyPress==VK_SPACE) or (keyPress==VK_RETURN)  then
			if newMenu[current][2]==nil then
				returnValue=newMenu[current][4]
				break
			else
				local r=newMenu[current][2](newMenu,current)               --调用菜单函数
				if r==1 then
					returnValue= -newMenu[current][4]
					break
				else
					Cls(x1,y1,x1+w,y1+h)
					if isBox==1 then
						DrawDialogBorder(x1,y1,x1+w,y1+h,C_WHITE)
					end
				end
			end
		end
	end
	--Cls(x1,y1,x1+w+1,y1+h+1,0,1)
	return returnValue
end


