function JY_Main()        --主程序入口
	xpcall(JY_Main_sub, OnError)     --捕获调用错误
end

function OnError(err)      --错误处理，打印错误信息
	lib.Log(err)                 --输出错误信息
	lib.Log(debug.traceback())   --输出调用堆栈信息
end

function JY_Main_sub()        --真正的游戏主程序入口
	SetGlobalConst()    --设置全程变量CC, 程序使用的常量
	SetGlobal()         --设置全程变量JY
	GenTalkIdx()        --生成对话idx
	SetModify()         --设置对函数的修改，定义新的物品，事件等等

	--禁止访问全程变量
	setmetatable(_G, { __newindex = function (_,n)
			error("attempt read write to undeclared variable " .. n,2)
		end,
		__index = function (_,n)
			error("attempt read read to undeclared variable " .. n,2)
		end }  
	)
	math.randomseed(os.time())          --初始化随机数发生器
	lib.EnableKeyRepeat(CONFIG.key_latency, CONFIG.key_repeat_interval)   --设置键盘重复率
	JY.game_status=GAME_STATUS_START
	--lib.PicInit(CC.PaletteFile)       --加载原来的256色调色板

	Cls()

	PlayMIDI(16)
	lib.FadeIn(50)

	local menu = GetNewGameMenu()

	--local menux=(CC.ScreenW-4*CC.StartMenuFontSize-2*CC.MenuBorderPixel)/2
	local menu_cx, menu_cy = GetVerticalMenuSize(menu)
	local menux = (CC.ScreenW - menu_cx) / 2
	local menuy = CC.ScreenH - menu_cy
	--local menuReturn = ShowVerticalMenu(menu,3,0,menux,menuy,0,0,0,0,CC.StartMenuFontSize,C_STARTMENU, C_RED)
	local menuReturn = ShowVerticalMenu3(menu, menux, menuy) 

	if menuReturn == 1 then        --重新开始游戏
		Cls()
		--DrawText(menux,CC.StartMenuY,"请稍候…",C_RED,CC.StartMenuFontSize)
		DrawText(menux, menuy,"请稍候…",C_RED,CC.StartMenuFontSize)
		UpdateScreen()
		CreateNewGame()          --设置新游戏数据
		JY.subscene = CC.NewGameSceneID         --新游戏直接进入场景
		JY.scenes[JY.subscene]["名称"]=JY.roles[0]["姓名"] .. "居"
		JY.base["人X1"] = CC.NewGameSceneX
		JY.base["人Y1"] = CC.NewGameSceneY
		JY.MyPic = CC.NewPersonPic
		lib.FadeOut(50)
		JY.game_status=GAME_STATUS_SCENE
		JY.mainmap_music=-1
		CollectGarbage()
		InitScene(false)
		if CC.NewGameEvent>0 then
			oldCallEvent(CC.NewGameEvent)
		end
	elseif menuReturn == 2 then         --载入旧的进度
		Cls()
		local menu = GetRecordMenu()
		menu.fontSize = CC.StartMenuFontSize
		menu.borderColor = C_STARTMENU
		menu.fontColor = C_STARTMENU
		menu.activeItemColor = C_RED
		menu.hasBorder = false
		menu.canExit = false
