function RunMainMapFrame()      --主地图

	local direct = -1
	local keypress = lib.GetKey()

	-- Hack by Neo
	-- exit the gmae
	if keypress == -2 then
		JY.game_status = GAME_STATUS_END
		return
	end

	--[[
	if keypress ~= -1 then
		JY.MyTick=0
		if keypress==VK_ESCAPE then
			ShowMainMenu()
			if JY.game_status==GAME_STATUS_MAINMAP_INIT then
				return 
			end
			JY.old_mainmap_x=-1         --强制重绘
			JY.old_mainmap_y=-1
		elseif keypress==VK_UP then
			direct=0
		elseif keypress==VK_DOWN then
			direct=3
		elseif keypress==VK_LEFT then
			direct=2
		elseif keypress==VK_RIGHT then
			direct=1
		end
	end
	--]]
	
	if keypress ~= -1 then
		JY.MyTick = 0
		local case = {
			[VK_ESCAPE] = function()
				ShowMainMenu()
				if JY.game_status == GAME_STATUS_MAINMAP_INIT then
					return
				end
				JY.old_mainmap_x = -1
				JY.old_mainmap_y = -1
			end,

			[VK_UP] = function()
				direct = 0
			end,

			[VK_DOWN] = function()
				direct = 3
			end,

			[VK_LEFT] = function()
				direct = 2
			end,

			[VK_RIGHT] = function()
				direct = 1
			end
		}

		if case[keypress] then
			case[keypress]()
		end
	end


	local x,y              --按照方向键要到达的坐标
	if direct ~= -1 then   --按下了光标键
		AddMyCurrentPic()         --增加主角贴图编号，产生走路效果
		x=JY.base["人X"]+CC.DirectX[direct+1]
		y=JY.base["人Y"]+CC.DirectY[direct+1]
		JY.base["人方向"]=direct
	else
		x=JY.base["人X"]
		y=JY.base["人Y"]
	end

	JY.subscene=CanEnterScene(x,y)   --判断是否进入子场景

	if lib.GetMMap(x,y,3)==0 and lib.GetMMap(x,y,4)==0 then     --没有建筑，可以到达
		JY.base["人X"]=x
		JY.base["人Y"]=y
	end
	JY.base["人X"]=Clamp(JY.base["人X"],10,CC.MWidth-10)           --限制坐标不能超出范围
	JY.base["人Y"]=Clamp(JY.base["人Y"],10,CC.MHeight-10)

	if CC.MMapBoat[lib.GetMMap(JY.base["人X"],JY.base["人Y"],0)]==1 then
		JY.base["乘船"]=1
	else
		JY.base["乘船"]=0
	end

	local pic=GetMyPic()

	lib.DrawMMap(JY.base["人X"],JY.base["人Y"],pic)             --显示主地图

	if CC.showCoordinate then
		DrawText(10,CC.ScreenH-20,string.format("%d %d",JY.base["人X"],JY.base["人Y"]) ,C_GOLD,16)
	end

	UpdateScreen()
	lib.SetClip(0,0,0,0)

	JY.old_mainmap_x = JY.base["人X"]
	JY.old_mainmap_y = JY.base["人Y"]
	JY.old_mainmap_pic = pic

	if JY.subscene >= 0 then          --进入子场景
		CollectGarbage()
		lib.UnloadMMap()
		lib.PicInit()
		--lib.AudioFadeOut(1000)
		lib.FadeOut(50)

		JY.game_status=GAME_STATUS_SCENE
		JY.mainmap_music=-1

		JY.MyPic=GetMyPic()
		JY.base["人X1"]=JY.scenes[JY.subscene]["入口X"]
		JY.base["人Y1"]=JY.scenes[JY.subscene]["入口Y"]

		InitScene(true)
	end

end

