--场景是否可进
--id 场景代号
--x,y 当前主地图坐标
--返回：场景id，-1表示没有场景可进
function CanEnterScene(x,y)         --场景是否可进
	if JY.EnterSceneXY==nil then    --如果为空，则重新产生数据。
		Cal_EnterSceneXY()
	end

	local id=JY.EnterSceneXY[y*CC.MWidth+x]
	if id~=nil then
		local e=JY.scenes[id]["进入条件"]
		if e==0 then        --可进
			return id
		elseif e==1 then    --不可进
			return -1
		elseif e==2 then    --有轻功高者进
			for i=1,CC.TeamNum do
				local pid=JY.base["队伍" .. i]
				if pid>=0 then
					if JY.roles[pid]["轻功"]>=70 then
						return id
					end
				end
			end
		end
	end
	return -1
end

function Cal_EnterSceneXY()   --计算哪些坐标可以进入场景
	JY.EnterSceneXY={}
	for id = 0,JY.num_scenes-1 do
		local scenes=JY.scenes[id]
		if scenes["外景入口X1"]>0 and scenes["外景入口Y1"] then
			JY.EnterSceneXY[scenes["外景入口Y1"]*CC.MWidth+scenes["外景入口X1"]]=id
		end
		if scenes["外景入口X2"]>0 and scenes["外景入口Y2"] then
			JY.EnterSceneXY[scenes["外景入口Y2"]*CC.MWidth+scenes["外景入口X2"]]=id
		end
	end
end

