-- 左右键翻页，上下键换队友
function ShowTeamatesStatus(teamate_id)---显示队友状态
	local page = 1
	local num_pages = 2
	local num_teamates = GetNumTeamates()

	while true do
		Cls()
		local id=JY.base["队伍" .. teamate_id]
		ShowRoleStatus(id, page)
		UpdateScreen()
		local keypress=WaitKey()
		lib.Delay(100)
		if keypress==VK_ESCAPE then
			break
		elseif keypress==VK_UP then
			teamate_id=teamate_id-1
		elseif keypress==VK_DOWN then
			teamate_id=teamate_id+1
		elseif keypress==VK_LEFT then
			page=page-1
		elseif keypress==VK_RIGHT then
			page=page+1
		end
		teamate_id = Clamp(teamate_id, 1, num_teamates)
		page = Clamp(page,1,num_pages)
	end
end

