--计算人物修炼成功需要的点数
--id 人物id
function TrainNeedExp(id)         --计算人物修炼物品成功需要的点数
	local thingid=JY.roles[id]["修炼物品"]
	local r =0
	if thingid >= 0 then
		if JY.items[thingid]["练出武功"] >=0 then
			local level=0          --此处的level是实际level-1。这样没有武功r和炼成一级是一样的。
			for i =1,10 do               -- 查找当前已经炼成武功等级
				if JY.roles[id]["武功" .. i]==JY.items[thingid]["练出武功"] then
					level=math.modf(JY.roles[id]["武功等级" .. i] /100)
					break
				end
			end
			if level <9 then
				r=(7-math.modf(JY.roles[id]["资质"]/15))*JY.items[thingid]["需经验"]*(level+1)
			else
				r=math.huge
			end
		else
			r=(7-math.modf(JY.roles[id]["资质"]/15))*JY.items[thingid]["需经验"]*2
		end
	end
	return r
end

