--执行医疗
--id1 医疗id2, 返回id2生命增加点数
function DoHeal(id1,id2)      --执行医疗
	if JY.roles[id1]["体力"]<50 then
		return 0
	end

	local add=JY.roles[id1]["医疗能力"]
	local value=JY.roles[id2]["受伤程度"]
	if value > add+20 then
		return 0
	end

	if value <25 then    --根据受伤程度计算实际医疗能力
		add=add*4/5
	elseif value <50 then
		add=add*3/4
	elseif value <75 then
		add=add*2/3
	else
		add=add/2
	end
	add=math.modf(add)+Random(5)

	AddPersonAttrib(id2,"受伤程度",-add)
	return AddPersonAttrib(id2,"生命",add)
end

