--新的显示物品菜单，模仿原游戏
--显示物品菜单
--返回选择的物品编号, -1表示没有选择
function SelectThing(items,num_items)    --显示物品菜单

	local xnum=CC.MenuThingXnum
	local ynum=CC.MenuThingYnum
	local borderPixel = 3

	local w=CC.ThingPicWidth*xnum+(xnum-1)*CC.ThingGapIn+2*CC.ThingGapOut  --总体宽度
	local h=CC.ThingPicHeight*ynum+(ynum-1)*CC.ThingGapIn+2*CC.ThingGapOut --物品栏高度

	local item_spacing = 2
	local dx=(CC.ScreenW-w)/2
	--local dy=(CC.ScreenH-h-2*(CC.ThingFontSize+2*CC.MenuBorderPixel+5))/2
	local dy=(CC.ScreenH-h-2*(CC.ThingFontSize+2*borderPixel+item_spacing))/2


	local y1_1,y1_2,y2_1,y2_2,y3_1,y3_2                  --名称，说明和图片的Y坐标

	local cur_line=0
	local cur_x=0
	local cur_y=0
	local cur_thing=-1

	while true do
		Cls()
		y1_1=dy
		y1_2=y1_1+CC.ThingFontSize+2*borderPixel
		DrawDialogBorder(dx,y1_1,dx+w,y1_2,C_WHITE)
		y2_1=y1_2 + item_spacing
		y2_2=y2_1+CC.ThingFontSize+2*borderPixel
		DrawDialogBorder(dx,y2_1,dx+w,y2_2,C_WHITE)
		y3_1=y2_2 + item_spacing
		y3_2=y3_1+h
		DrawDialogBorder(dx,y3_1,dx+w,y3_2,C_WHITE)

		for y=0,ynum-1 do
			for x=0,xnum-1 do
				local id=y*xnum+x+xnum*cur_line         --当前待选择物品
				local boxcolor
				if x==cur_x and y==cur_y then
					boxcolor=C_WHITE
					if items[id]>=0 then
						cur_thing=items[id]
						local str=JY.items[items[id]]["名称"]
						if JY.items[items[id]]["类型"]==1 or JY.items[items[id]]["类型"]==2 then
							if JY.items[items[id]]["使用人"] >=0 then
								str=str .. "(" .. JY.roles[JY.items[items[id]]["使用人"]]["姓名"] .. ")"
							end
						end
						str=string.format("%s × %d",str,num_items[id])
						local str2=JY.items[items[id]]["物品说明"]

						DrawText(dx+CC.ThingGapOut,y1_1+borderPixel,str,C_GOLD,CC.ThingFontSize)
						DrawText(dx+CC.ThingGapOut,y2_1+borderPixel,str2,C_ORANGE,CC.ThingFontSize)

					else
						cur_thing=-1
					end
				else
					boxcolor=C_BLACK
				end
				local boxx=dx+CC.ThingGapOut+x*(CC.ThingPicWidth+CC.ThingGapIn)
				local boxy=y3_1+CC.ThingGapOut+y*(CC.ThingPicHeight+CC.ThingGapIn)
				lib.DrawRect(boxx,boxy,boxx+CC.ThingPicWidth+1,boxy+CC.ThingPicHeight+1,boxcolor)
				if items[id]>=0 then
					if CC.LoadThingPic==1 then
						lib.DrawCachedImage(2,items[id]*2,boxx+1,boxy+1,1)
					else
						lib.DrawCachedImage(0,(items[id]+CC.StartThingPic)*2,boxx+1,boxy+1,1)
					end
				end
			end
		end

		UpdateScreen()
		local keypress=WaitKey()
		lib.Delay(100)
		if keypress==VK_ESCAPE then
			cur_thing=-1
			break
		elseif keypress==VK_RETURN or keypress==VK_SPACE then
			break
		elseif keypress==VK_UP then
			if  cur_y == 0 then
				if  cur_line > 0 then
					cur_line = cur_line - 1
				end
			else
				cur_y = cur_y - 1
			end
		elseif keypress==VK_DOWN then
			if  cur_y ==ynum-1 then
				if  cur_line < (math.modf(200/xnum)-ynum) then
					cur_line = cur_line + 1
				end
			else
				cur_y = cur_y + 1
			end
		elseif keypress==VK_LEFT then
			if  cur_x > 0 then
				cur_x=cur_x-1
			else
				cur_x=xnum-1
			end
		elseif keypress==VK_RIGHT then
			if  cur_x ==xnum-1 then
				cur_x=0
			else
				cur_x=cur_x+1
			end
		end

	end

	Cls()
	return cur_thing
end


--场景处理主函数
function RunSceneFrame()         --场景处理主函数

	DrawSMap(0) -- <ZG> 我把FastShowScreen控制变量去掉了，DrawSMap(0) 可以显示场景中的动画

	if CC.showCoordinate then
		DrawText(10,CC.ScreenH-20,string.format("%s %d %d",JY.scenes[JY.subscene]["名称"],JY.base["人X1"],JY.base["人Y1"]) ,C_GOLD,16)
	end

	UpdateScreen()

	lib.SetClip(0,0,0,0)

	local d_pass=GetS(JY.subscene,JY.base["人X1"],JY.base["人Y1"],3)   --当前路过事件
	if d_pass>=0 then
		if d_pass ~=JY.OldDPass then     --避免重复触发
			EventExecute(d_pass, TT_PASSING_BY)       --路过触发事件
			JY.OldDPass=d_pass
			JY.old_scene_x=-1
			JY.old_scene_y=-1
			JY.D_Valid=nil
		end
	else
		JY.OldDPass=-1
	end

	local exit=false                --是否碰到出口
	if (JY.scenes[JY.subscene]["出口X1"] ==JY.base["人X1"] and JY.scenes[JY.subscene]["出口Y1"] ==JY.base["人Y1"]) or
		(JY.scenes[JY.subscene]["出口X2"] ==JY.base["人X1"] and JY.scenes[JY.subscene]["出口Y2"] ==JY.base["人Y1"]) or
		(JY.scenes[JY.subscene]["出口X3"] ==JY.base["人X1"] and JY.scenes[JY.subscene]["出口Y3"] ==JY.base["人Y1"]) then
		exit=true
	end

	if exit then    --出去，返回主地图
		JY.game_status=GAME_STATUS_MAINMAP

		lib.PicInit()
		CollectGarbage()
		--lib.AudioFadeOut(1000)
		lib.FadeOut(50)

		if JY.mainmap_music < 0 then
			JY.mainmap_music=JY.scenes[JY.subscene]["出门音乐"]
			lib.Log(JY.scenes[JY.subscene]["出门音乐"])
		end

		InitMainMap()

		JY.subscene=-1
		JY.old_scene_x=-1
		JY.old_scene_y=-1

		lib.DrawMMap(JY.base["人X"],JY.base["人Y"],GetMyPic())
		lib.FadeIn(50)
		--lib.GetKey()
		ClearKeyBuffer()
		return
	end

	--是否跳转到其他场景
	if JY.scenes[JY.subscene]["跳转场景"] >=0 then
		if JY.base["人X1"]==JY.scenes[JY.subscene]["跳转口X1"] and JY.base["人Y1"]==JY.scenes[JY.subscene]["跳转口Y1"] then
			JY.subscene=JY.scenes[JY.subscene]["跳转场景"]
			lib.FadeOut(50)

			if JY.scenes[JY.subscene]["外景入口X1"]==0 and JY.scenes[JY.subscene]["外景入口Y1"]==0 then
				JY.base["人X1"]=JY.scenes[JY.subscene]["入口X"]            --新场景的外景入口为0，表示这是一个内部场景
				JY.base["人Y1"]=JY.scenes[JY.subscene]["入口Y"]
			else
				JY.base["人X1"]=JY.scenes[JY.subscene]["跳转口X2"]         --外部场景，即从其他内部场景跳回。
				JY.base["人Y1"]=JY.scenes[JY.subscene]["跳转口Y2"]
			end

			InitScene(true)

			return
		end
	end

	local x,y
	local keypress = lib.GetKey()
	local direct=-1
	if keypress ~= -1 then
		JY.MyTick=0
		if keypress==VK_ESCAPE then
			ShowMainMenu()
			JY.old_scene_x = -1
			JY.old_scene_y = -1
		elseif keypress == VK_UP then
			direct=0
		elseif keypress == VK_DOWN then
			direct=3
		elseif keypress==VK_LEFT then
			direct=2
		elseif keypress==VK_RIGHT then
			direct=1
		elseif keypress==VK_SPACE or keypress==VK_RETURN  then
			if JY.base["人方向"]>=0 then        --当前方向下一个位置
				local d_num=GetS(JY.subscene,JY.base["人X1"]+CC.DirectX[JY.base["人方向"]+1],JY.base["人Y1"]+CC.DirectY[JY.base["人方向"]+1],3)
				if d_num>=0 then
					EventExecute(d_num, TT_DIGGING)       --空格触发事件
					JY.old_scene_x=-1
					JY.old_scene_y=-1
					JY.D_Valid=nil
				end
			end
		end
	end

	if JY.game_status ~= GAME_STATUS_SCENE then
		return 
	end

	if direct ~= -1 then
		AddMyCurrentPic()
		x=JY.base["人X1"]+CC.DirectX[direct+1]
		y=JY.base["人Y1"]+CC.DirectY[direct+1]
		JY.base["人方向"]=direct
	else
		x=JY.base["人X1"]
		y=JY.base["人Y1"]
	end

	JY.MyPic=GetMyPic()

	DtoSMap()

	if IsScenePassable(x,y)==true then          --新的坐标可以走过去
		JY.base["人X1"]=x
		JY.base["人Y1"]=y
	end

	JY.base["人X1"]=Clamp(JY.base["人X1"],1,CC.SWidth-2)
	JY.base["人Y1"]=Clamp(JY.base["人Y1"],1,CC.SHeight-2)

end

--场景坐标(x,y)是否可以通过
--返回true,可以，false不能
function IsScenePassable(x,y)  --场景坐标(x,y)是否可以通过
	local passable = true        --是否可以通过

	if GetS(JY.subscene,x,y,1)>0 then     --场景层1有物品，不可通过
		passable = false
	end

	local d_data = GetS(JY.subscene,x,y,3)     --事件层4
	if d_data >= 0 then
		if GetD(JY.subscene,d_data,0) ~= 0 then  --d*数据为不能通过
			passable = false
		end
	end

	if CC.SceneWater[GetS(JY.subscene,x,y,0)] then   --水面，不可进入
		passable = false
	end
	return passable
end


function Cal_D_Valid()     --计算200个D中有效的D
	if JY.D_Valid~=nil then
		return 
	end

	local sceneid=JY.subscene
	JY.D_Valid={}
	JY.D_Valid_Num=0
	for i=0,CC.DNum-1 do
		local x=GetD(sceneid,i,9)
		local y=GetD(sceneid,i,10)
		local v=GetS(sceneid,x,y,3)
		if v>=0 then
			JY.D_Valid[JY.D_Valid_Num]=i
			JY.D_Valid_Num=JY.D_Valid_Num+1
		end
	end
end

function DtoSMap()          ---D*中的事件处理动画效果。
	local sceneid=JY.subscene
	JY.NumD_PicChange=0
	JY.D_PicChange={}

	if JY.D_Valid==nil then
		Cal_D_Valid()
	end

	for k=0,JY.D_Valid_Num-1 do
		local i=JY.D_Valid[k]

		local p1=GetD(sceneid,i,5)
		if p1>0 then
			local p2=GetD(sceneid,i,6)
			local p3=GetD(sceneid,i,7)
			if p1 ~= p2 then
				local old_p3=p3
				local delay=GetD(sceneid,i,8)
				if not (p3>=CC.SceneFlagPic[1]*2 and p3<=CC.SceneFlagPic[2]*2 and CC.ShowFlag==0) then --是否显示旗帜
					if p3<=p1 then     --动画已停止
						if JY.MyTick2 %100 > delay then
							p3=p3+2
						end
					else
						if JY.MyTick2 % 4 ==0 then      --4个节拍动画增加一次
							p3=p3+2
						end
					end
					if p3>p2 then
						p3=p1
					end
				end
				if old_p3 ~=p3 then    --动画改变了，增加一个
					local x=GetD(sceneid,i,9)
					local y=GetD(sceneid,i,10)
					local dy=GetS(sceneid,x,y,4)       --海拔
					JY.D_PicChange[JY.NumD_PicChange]={x=x, y=y, dy=dy, p1=old_p3/2, p2=p3/2}
					JY.NumD_PicChange=JY.NumD_PicChange+1
					SetD(sceneid,i,7,p3)
				end
			end
		end
	end
end

--fastdraw = 0 or nil 全部重绘。用于事件中
--           1 考虑脏矩形 用于显示场景循环
function DrawSMap(fastdraw)         --绘场景地图
	if fastdraw==nil then
		fastdraw=0
	end
	local x0=JY.subscene_x+JY.base["人X1"]-1    --绘图中心点
	local y0=JY.subscene_y+JY.base["人Y1"]-1

	local x=Clamp(x0,CC.SceneXMin,CC.SceneXMax)-JY.base["人X1"]
	local y=Clamp(y0,CC.SceneYMin,CC.SceneYMax)-JY.base["人Y1"]

	if fastdraw==0 then
		lib.DrawSMap(JY.subscene,JY.base["人X1"],JY.base["人Y1"],x,y,JY.MyPic)
	else
		if JY.old_scene_x>=0 and JY.old_scene_y>=0 and
			JY.old_scene_x+JY.old_scene_xoff==JY.base["人X1"]+x and         --绘图中心点不变，人走路也可以用裁剪方式绘图
			JY.old_scene_y+JY.old_scene_yoff==JY.base["人Y1"]+y then

			local num_clip=0
			local clip={}

			for i=0,JY.NumD_PicChange-1 do   --计算D*中贴图改变的矩形
				local dx=JY.D_PicChange[i].x-JY.base["人X1"]-x
				local dy=JY.D_PicChange[i].y-JY.base["人Y1"]-y
				clip[num_clip]=Cal_PicClip(dx,dy,JY.D_PicChange[i].p1,0,
				dx,dy,JY.D_PicChange[i].p2,0 )
				clip[num_clip].y1=clip[num_clip].y1-JY.D_PicChange[i].dy
				clip[num_clip].y2=clip[num_clip].y2-JY.D_PicChange[i].dy
				num_clip=num_clip+1
			end

			if JY.old_scene_pic>=0 then  --计算主角矩形
				if not ( JY.old_scene_x==JY.base["人X1"] and    --主角有变化
					JY.old_scene_y==JY.base["人Y1"] and
					JY.old_scene_pic==JY.MyPic ) then
					local dy1=GetS(JY.subscene,JY.base["人X1"],JY.base["人Y1"],4)   --海拔
					local dy2=GetS(JY.subscene,JY.old_scene_x,JY.old_scene_y,4)
					local dy=math.max(dy1,dy2)
					clip[num_clip]=Cal_PicClip(-JY.old_scene_xoff,-JY.old_scene_yoff,JY.old_scene_pic,0,
					-x,-y,JY.MyPic,0)
					clip[num_clip].y1=clip[num_clip].y1- dy
					clip[num_clip].y2=clip[num_clip].y2- dy
					num_clip=num_clip+1
				end
			end

			local area=0          --计算所有脏矩形面积
			for i=0,num_clip-1 do
				clip[i]=ClipRect(clip[i])    --矩形屏幕剪裁
				if clip[i]~=nil then
					area=area+(clip[i].x2-clip[i].x1)*(clip[i].y2-clip[i].y1)
				end
			end

			if area <CC.ScreenW*CC.ScreenH/2 and num_clip<15 then        --面积足够小，矩形数目少，则更新脏矩形。
				for i=0,num_clip-1 do
					if clip[i]~=nil then
						lib.SetClip(clip[i].x1,clip[i].y1,clip[i].x2,clip[i].y2)
						lib.DrawSMap(JY.subscene,JY.base["人X1"],JY.base["人Y1"],x,y,JY.MyPic)
					end
				end
			else    --面积太大，直接重绘
				lib.SetClip(0,0,CC.ScreenW,CC.ScreenH)   --由于redraw=0，必须给出裁剪矩形以后才能UpdateScreen
				lib.DrawSMap(JY.subscene,JY.base["人X1"],JY.base["人Y1"],x,y,JY.MyPic)
			end
		else
			lib.SetClip(0,0,CC.ScreenW,CC.ScreenH)
			lib.DrawSMap(JY.subscene,JY.base["人X1"],JY.base["人Y1"],x,y,JY.MyPic)
		end
	end

	JY.old_scene_x = JY.base["人X1"]
	JY.old_scene_y = JY.base["人Y1"]
	JY.old_scene_pic = JY.MyPic
	JY.old_scene_xoff = x
	JY.old_scene_yoff = y
end


-- 读取游戏进度
-- id=0 新进度，=1/2/3 进度
--
--这里是先把数据读入Byte数组中。然后定义访问相应表的方法，在访问表时直接从数组访问。
--与以前的实现相比，从文件中读取和保存到文件的时间显著加快。而且内存占用少了
function LoadRecord(id)       -- 读取游戏进度
	local t1=lib.GetTime()

	--读取R*.idx文件
	local data=Byte.create(6*4)
	Byte.loadfile(data,CC.R_IDXFilename[id],0,6*4)

	local idx={}
	idx[0]=0
	for i =1,6 do
		idx[i]=Byte.get32(data,4*(i-1))
	end

	--读取R*.grp文件
	JY.Data_Base=Byte.create(idx[1]-idx[0])              --基本数据
	Byte.loadfile(JY.Data_Base,CC.R_GRPFilename[id],idx[0],idx[1]-idx[0])

	--设置访问基本数据的方法，这样就可以用访问表的方式访问了。而不用把二进制数据转化为表。节约加载时间和空间
	local meta_t={
		__index=function(t,k)
			return GetDataFromStruct(JY.Data_Base, 0, CC.Base_S, k)
		end,

		__newindex=function(t,k,v)
			SetDataFromStruct(JY.Data_Base, 0, CC.Base_S, k, v)
		end
	}
	setmetatable(JY.base, meta_t)


	JY.num_roles=math.floor((idx[2]-idx[1])/CC.PersonSize)   --人物

	JY.Data_Person=Byte.create(CC.PersonSize*JY.num_roles)
	Byte.loadfile(JY.Data_Person,CC.R_GRPFilename[id],idx[1],CC.PersonSize*JY.num_roles)

	for i=0,JY.num_roles-1 do
		JY.roles[i]={}
		local meta_t={
			__index=function(t,k)
				return GetDataFromStruct(JY.Data_Person,i*CC.PersonSize,CC.Person_S,k)
			end,

			__newindex=function(t,k,v)
				SetDataFromStruct(JY.Data_Person,i*CC.PersonSize,CC.Person_S,k,v)
			end
		}
		setmetatable(JY.roles[i],meta_t)
	end

	JY.num_items=math.floor((idx[3]-idx[2])/CC.ThingSize)     --物品
	JY.Data_Thing=Byte.create(CC.ThingSize*JY.num_items)
	Byte.loadfile(JY.Data_Thing,CC.R_GRPFilename[id],idx[2],CC.ThingSize*JY.num_items)
	for i=0,JY.num_items-1 do
		JY.items[i]={}
		local meta_t={
			__index=function(t,k)
				return GetDataFromStruct(JY.Data_Thing,i*CC.ThingSize,CC.Thing_S,k)
			end,

			__newindex=function(t,k,v)
				SetDataFromStruct(JY.Data_Thing,i*CC.ThingSize,CC.Thing_S,k,v)
			end
		}
		setmetatable(JY.items[i],meta_t)
	end

	JY.num_scenes=math.floor((idx[4]-idx[3])/CC.SceneSize)     --场景
	JY.Data_Scene=Byte.create(CC.SceneSize*JY.num_scenes)
	Byte.loadfile(JY.Data_Scene,CC.R_GRPFilename[id],idx[3],CC.SceneSize*JY.num_scenes)
	for i=0,JY.num_scenes-1 do
		JY.scenes[i]={}
		local meta_t={
			__index=function(t,k)
				return GetDataFromStruct(JY.Data_Scene,i*CC.SceneSize,CC.Scene_S,k)
			end,

			__newindex=function(t,k,v)
				SetDataFromStruct(JY.Data_Scene,i*CC.SceneSize,CC.Scene_S,k,v)
			end
		}
		setmetatable(JY.scenes[i],meta_t)
	end

	JY.num_kongfus=math.floor((idx[5]-idx[4])/CC.WugongSize)     --武功
	JY.Data_Wugong=Byte.create(CC.WugongSize*JY.num_kongfus)
	Byte.loadfile(JY.Data_Wugong,CC.R_GRPFilename[id],idx[4],CC.WugongSize*JY.num_kongfus)
	for i=0,JY.num_kongfus-1 do
		JY.kongfus[i]={}
		local meta_t={
			__index=function(t,k)
				return GetDataFromStruct(JY.Data_Wugong,i*CC.WugongSize,CC.Wugong_S,k)
			end,

			__newindex=function(t,k,v)
				SetDataFromStruct(JY.Data_Wugong,i*CC.WugongSize,CC.Wugong_S,k,v)
			end
		}
		setmetatable(JY.kongfus[i],meta_t)
	end

	JY.num_shops=math.floor((idx[6]-idx[5])/CC.ShopSize)     --小宝商店
	JY.Data_Shop=Byte.create(CC.ShopSize*JY.num_shops)
	Byte.loadfile(JY.Data_Shop,CC.R_GRPFilename[id],idx[5],CC.ShopSize*JY.num_shops)
	for i=0,JY.num_shops-1 do
		JY.shops[i]={}
		local meta_t={
			__index=function(t,k)
				return GetDataFromStruct(JY.Data_Shop,i*CC.ShopSize,CC.Shop_S,k)
			end,

			__newindex=function(t,k,v)
				SetDataFromStruct(JY.Data_Shop,i*CC.ShopSize,CC.Shop_S,k,v)
			end
		}
		setmetatable(JY.shops[i],meta_t)

	end

	--lib.LoadSMap(CC.S_Filename[id],CC.TempS_Filename,JY.num_scenes,CC.SWidth,CC.SHeight,CC.D_Filename[id],CC.DNum,11)
	lib.LoadSMap(CC.S_Filename[id], CC.D_Filename[id])
	collectgarbage()

	lib.Log(string.format("Loadrecord time=%d",lib.GetTime()-t1))
end

-- 写游戏进度
-- id=0 新进度，=1/2/3 进度
function SaveRecord(id)         -- 写游戏进度
	--读取R*.idx文件
	local t1=lib.GetTime()

	local data=Byte.create(6*4)
	Byte.loadfile(data,CC.R_IDXFilename[id],0,6*4)

	local idx={}
	idx[0]=0
	for i =1,6 do
		idx[i]=Byte.get32(data,4*(i-1))
	end

	--写R*.grp文件
	Byte.savefile(JY.Data_Base,CC.R_GRPFilename[id],idx[0],idx[1]-idx[0])
	Byte.savefile(JY.Data_Person,CC.R_GRPFilename[id],idx[1],CC.PersonSize*JY.num_roles)
	Byte.savefile(JY.Data_Thing,CC.R_GRPFilename[id],idx[2],CC.ThingSize*JY.num_items)
	Byte.savefile(JY.Data_Scene,CC.R_GRPFilename[id],idx[3],CC.SceneSize*JY.num_scenes)
	Byte.savefile(JY.Data_Wugong,CC.R_GRPFilename[id],idx[4],CC.WugongSize*JY.num_kongfus)
	Byte.savefile(JY.Data_Shop,CC.R_GRPFilename[id],idx[5],CC.ShopSize*JY.num_shops)
	lib.SaveSMap(CC.S_Filename[id],CC.D_Filename[id])
	lib.Log(string.format("SaveRecord time=%d",lib.GetTime()-t1))

end
