--判断一个人是否可以装备或修炼一个物品
--返回 true可以修炼，false不可
function CanUseThing(id,personid)           --判断一个人是否可以装备或修炼一个物品
	local str=""
	if JY.items[id]["仅修炼人物"] >=0 then
		if JY.items[id]["仅修炼人物"] ~= personid then
			return false
		end
	end

	if JY.items[id]["需内力性质"] ~=2 and JY.roles[personid]["内力性质"] ~=2 then
		if JY.items[id]["需内力性质"] ~= JY.roles[personid]["内力性质"] then
			return false
		end
	end

	if JY.items[id]["需内力"] > JY.roles[personid]["内力最大值"] then
		return false
	end
	if JY.items[id]["需攻击力"] > JY.roles[personid]["攻击力"] then
		return false
	end
	if JY.items[id]["需轻功"] > JY.roles[personid]["轻功"] then
		return false
	end
	if JY.items[id]["需用毒能力"] > JY.roles[personid]["用毒能力"] then
		return false
	end
	if JY.items[id]["需医疗能力"] > JY.roles[personid]["医疗能力"] then
		return false
	end
	if JY.items[id]["需解毒能力"] > JY.roles[personid]["解毒能力"] then
		return false
	end
	if JY.items[id]["需拳掌功夫"] > JY.roles[personid]["拳掌功夫"] then
		return false
	end
	if JY.items[id]["需御剑能力"] > JY.roles[personid]["御剑能力"] then
		return false
	end
	if JY.items[id]["需耍刀技巧"] > JY.roles[personid]["耍刀技巧"] then
		return false
	end
	if JY.items[id]["需特殊兵器"] > JY.roles[personid]["特殊兵器"] then
		return false
	end
	if JY.items[id]["需暗器技巧"] > JY.roles[personid]["暗器技巧"] then
		return false
	end
	if JY.items[id]["需资质"] >= 0 then
		if JY.items[id]["需资质"] > JY.roles[personid]["资质"] then
			return false
		end
	else
		if -JY.items[id]["需资质"] < JY.roles[personid]["资质"] then
			return false
		end
	end

	return true
end


