	Cls()
	DrawStrBox(CC.MainSubMenuX,CC.MainSubMenuY,string.format("谁要修炼%s?",JY.items[id]["名称"]),C_WHITE,CC.DefaultFontSize)
	local nexty=CC.MainSubMenuY+CC.SingleLineHeight
