				DrawStrBoxWaitKey("此人不适合修炼此物品",C_WHITE,CC.DefaultFontSize)
				return 0
			end
		end


		if CanUseThing(id,personid) then
			if JY.items[id]["使用人"]==personid then
				return 0
			end

			if JY.roles[personid]["修炼物品"]>=0 then
				JY.items[JY.roles[personid]["修炼物品"]]["使用人"]=-1
			end

			if JY.items[id]["使用人"]>=0 then
				JY.roles[JY.items[id]["使用人"]]["修炼物品"]=-1
				JY.roles[JY.items[id]["使用人"]]["修炼点数"]=0
				JY.roles[JY.items[id]["使用人"]]["物品修炼点数"]=0
			end

			JY.items[id]["使用人"]=personid
			JY.roles[personid]["修炼物品"]=id
			JY.roles[personid]["修炼点数"]=0
			JY.roles[personid]["物品修炼点数"]=0
		else
			DrawStrBoxWaitKey("此人不适合修炼此物品",C_WHITE,CC.DefaultFontSize)
			return 0
		end
	end

	return 1
end

