--药品使用实际效果
--id 物品id，
--personid 使用人id
--返回值：0 使用没有效果，物品数量应该不变。1 使用有效果，则使用后物品数量应该-1
function UseThingEffect(id,personid)          --药品使用实际效果
	local str={}
	str[0]=string.format("使用 %s",JY.items[id]["名称"])
	local strnum=1
	local addvalue

	if JY.items[id]["加生命"] >0 then
		local add=JY.items[id]["加生命"]-math.modf(JY.roles[personid]["受伤程度"]/2)+Random(10)
		if add <=0 then
			add=5+Random(5)
		end
		AddPersonAttrib(personid,"受伤程度",-JY.items[id]["加生命"]/4)
		addvalue,str[strnum]=AddPersonAttrib(personid,"生命",add)
		if addvalue ~=0 then
			strnum=strnum+1
		end
	end

	local function ThingAddAttrib(s)     ---定义局部函数，处理吃药后增加属性
		if JY.items[id]["加" .. s] ~=0 then
			addvalue,str[strnum]=AddPersonAttrib(personid,s,JY.items[id]["加" .. s])
			if addvalue ~=0 then
				strnum=strnum+1
			end
		end
	end

	ThingAddAttrib("生命最大值")

	if JY.items[id]["加中毒解毒"] <0 then
		addvalue,str[strnum]=AddPersonAttrib(personid,"中毒程度",math.modf(JY.items[id]["加中毒解毒"]/2))
		if addvalue ~=0 then
			strnum=strnum+1
		end
	end

	ThingAddAttrib("体力")

	if JY.items[id]["改变内力性质"] ==2 then
		str[strnum]="内力门路改为阴阳合一"
		strnum=strnum+1
	end

	ThingAddAttrib("内力")
	ThingAddAttrib("内力最大值")
	ThingAddAttrib("攻击力")
	ThingAddAttrib("防御力")
	ThingAddAttrib("轻功")
	ThingAddAttrib("医疗能力")
	ThingAddAttrib("用毒能力")
	ThingAddAttrib("解毒能力")
