--产生对话显示需要的字符串，即每隔n个中文字符加一个星号
function GenerateTalkingText(str, charsPerLine)              --产生对话显示需要的字符串
	local tmpstr=""
	for s in string.gmatch(str .. "*","(.-)%*") do           --去掉对话中的所有*. 字符串尾部加一个星号，避免无法匹配
		tmpstr=tmpstr .. s
	end

	local newstr=""
	while #tmpstr>0 do
		local w=0
		while w<#tmpstr do
			local v=string.byte(tmpstr,w+1)          --当前字符的值
			if v>=128 then
				w=w+2
			else
				w=w+1
			end
			if w >= 2*charsPerLine-1 then     --为了避免跨段中文字符
				break
			end
		end

		if w<#tmpstr then
			if w==2*charsPerLine-1 and string.byte(tmpstr,w+1)<128 then
				newstr=newstr .. string.sub(tmpstr,1,w+1) .. "*"
				tmpstr=string.sub(tmpstr,w+2,-1)
			else
				newstr=newstr .. string.sub(tmpstr,1,w)  .. "*"
				tmpstr=string.sub(tmpstr,w+1,-1)
			end
		else
			newstr=newstr .. tmpstr
			break
		end
	end
	return newstr
end

