--复杂版本对话
--s 字符串，必须加上*作为分行，如果里面没有*,则会自动加上
function TalkEx(s,headid,flag)          --复杂版本对话
	local picw=CC.ScreenW / 6     -- 最大头像图片宽高
	local pich=CC.ScreenH / 4
	local talkxnum=12  -- 对话一行字数
	local talkynum=3   -- 对话行数
	local dx = CC.ScreenW / 16		-- 对话框离屏幕边界的距离
	local dy = CC.ScreenH / 12
	local boxpicw = picw + 10
	local boxpich = pich + 10
	local boxtalkw = 12 * CC.DefaultFontSize + 10
	local boxtalkh = boxpich

	local talkBorder=(pich-talkynum*CC.DefaultFontSize)/(talkynum+1)

	-- 显示头像和对话的坐标
	local xy = {
		[0] = {
			headx=dx,heady=dy,
			talkx=dx+boxpicw+2,talky=dy,
			showhead=1
		},
		{
			headx=CC.ScreenW-1-dx-boxpicw,heady=CC.ScreenH-dy-boxpich,
			talkx=CC.ScreenW-1-dx-boxpicw-boxtalkw-2,talky= CC.ScreenH-dy-boxpich,
			showhead=1
		},
		{
			headx=dx,heady=dy,
			talkx=dx+boxpicw+2,talky=dy,
			showhead=0
		},
		{
			headx=CC.ScreenW-1-dx-boxpicw,heady=CC.ScreenH-dy-boxpich,
			talkx=CC.ScreenW-1-dx-boxpicw-boxtalkw-2,talky= CC.ScreenH-dy-boxpich,
			showhead=1
		},
		{
			headx=CC.ScreenW-1-dx-boxpicw,heady=dy,
			talkx=CC.ScreenW-1-dx-boxpicw-boxtalkw-2,talky=dy,showhead=1
		},
		{
			headx=dx,heady=CC.ScreenH-dy-boxpich,talkx=dx+boxpicw+2,talky=CC.ScreenH-dy-boxpich,showhead=1
		}, 
	}

	if flag<0 or flag>5 then
		flag=0
	end

	if xy[flag].showhead==0 then
		headid=-1
	end

	if string.find(s,"*") ==nil then
		s=GenerateTalkingText(s, 12)
	end

	lib.EnableKeyRepeat(0, CONFIG.key_repeat_interval)
	--lib.GetKey()
	ClearKeyBuffer()
	local startp=1
	local endp
	local dy=0
	while true do
		if dy==0 then
			Cls()
			if headid>=0 then
				DrawDialogBorder(xy[flag].headx, xy[flag].heady, xy[flag].headx + boxpicw, xy[flag].heady + boxpich,C_WHITE)
				local w,h=lib.PicGetXY(1,headid*2)
				local x=(picw-w)/2
				local y=(pich-h)/2
				lib.DrawCachedImage(1,headid*2,xy[flag].headx+5+x,xy[flag].heady+5+y,1)
			end
			DrawDialogBorder(xy[flag].talkx, xy[flag].talky, xy[flag].talkx +boxtalkw, xy[flag].talky + boxtalkh,C_WHITE)
		end
		endp=string.find(s,"*",startp)
		if endp==nil then
			DrawText(xy[flag].talkx + 5, xy[flag].talky + 5+talkBorder + dy * (CC.DefaultFontSize+talkBorder),string.sub(s,startp),C_WHITE,CC.DefaultFontSize)
			UpdateScreen()
			WaitKey()
			break
		else
			DrawText(xy[flag].talkx + 5, xy[flag].talky + 5+talkBorder + dy * (CC.DefaultFontSize+talkBorder),string.sub(s,startp,endp-1),C_WHITE,CC.DefaultFontSize)
		end
		dy=dy+1
		startp=endp+1
		if dy>=talkynum then
			UpdateScreen()
			WaitKey()
			dy=0
		end
	end

	lib.EnableKeyRepeat(CONFIG.key_latency, CONFIG.key_repeat_interval)

	Cls()
end

