--从old_talk.lua中读取编号为stringId的字符串。
--需要的时候读取，可以节约内存占用，不用再把整个文件读入内存数据了。
function GetStringById(stringId)            --从文件读取一条对话
	local idxfile=CC.TalkIdxFile
	local grpfile=CC.TalkGrpFile

	local length=filelength(idxfile)

	if stringId<0 and stringId>=length/4 then
		return
	end

	local data=Byte.create(2*4)
	local id1,id2
	if stringId==0 then
		Byte.loadfile(data,idxfile,0,4)
		id1=0
		id2=Byte.get32(data,0)
	else
		Byte.loadfile(data,idxfile,(stringId-1)*4,4*2)
		id1=Byte.get32(data,0)
		id2=Byte.get32(data,4)
	end

	-- local p=io.open(grpfile,"r")
	-- p:seek("set",id1)
	-- local string = p:read("*line")
	-- p:close()
    local string = lib.GetDialogString(id1)

	return string
end


