--场景移动
--为简化，实际上是场景移动(x2-x1)，(y2-y1)。先y后x。因此，x1,y1可设为0
function instruct_25(x1,y1,x2,y2)             --场景移动
	local sign
	if y1 ~= y2 then
		if y2<y1 then
			sign=-1
		else
			sign=1
		end
		for i=y1+sign,y2,sign do
			local t1=lib.GetTime()
			JY.subscene_y=JY.subscene_y+sign
			--JY.old_scene_x=-1
			--JY.old_scene_y=-1
			DrawSMap()
			UpdateScreen()
			local t2=lib.GetTime()
			if (t2-t1)<CC.SceneMoveFrame then
				lib.Delay(CC.SceneMoveFrame-(t2-t1))
			end
		end
	end

	if x1 ~= x2 then
		if x2<x1 then
			sign=-1
		else
			sign=1
		end
		for i=x1+sign,x2,sign do
			local t1=lib.GetTime()
			JY.subscene_x=JY.subscene_x+sign
			--JY.old_scene_x=-1
			--JY.old_scene_y=-1

			DrawSMap()
			UpdateScreen()
			local t2=lib.GetTime()
			if (t2-t1)<CC.SceneMoveFrame then
				lib.Delay(CC.SceneMoveFrame-(t2-t1))
			end
		end
	end
end


