function instruct_41(personid,thingid,num)        --其他人员增加物品
	local k=0
	for i =1, 4 do        --已有物品
		if JY.roles[personid]["携带物品" .. i]==thingid then
			JY.roles[personid]["携带物品数量" .. i]=JY.roles[personid]["携带物品数量" .. i]+num
			k=i
			break
		end
	end

	--物品减少到0，则后面物品往前移动
	if k>0 and JY.roles[personid]["携带物品数量" .. k] <=0 then
		for i=k+1,4 do
			JY.roles[personid]["携带物品" .. i-1]=JY.roles[personid]["携带物品" .. i]
			JY.roles[personid]["携带物品数量" .. i-1]=JY.roles[personid]["携带物品数量" .. i]
		end
		JY.roles[personid]["携带物品" .. 4]=-1
		JY.roles[personid]["携带物品数量" .. 4]=0
	end


	if k==0 then    --没有物品，注意此处不考虑超过4个物品的情况，如果超过，则无法加入。
		for i =1, 4 do        --已有物品
			if JY.roles[personid]["携带物品" .. i]==-1 then
				JY.roles[personid]["携带物品" .. i]=thingid
				JY.roles[personid]["携带物品数量" .. i]=num
				break
			end
		end
	end
end


