--设置战斗全程变量
function WarSetGlobal()            --设置战斗全程变量
	WAR={}

	WAR.Data={}              --战斗信息，war.sta文件

	WAR.SelectPerson={}            --设置选择参战人  0 未选，1 选，不可取消，2 选，可取消。选择参展人菜单调用使用

	WAR.roles={}                 --战斗人物信息
	for i=0,26-1 do
		WAR.roles[i]={}
		WAR.roles[i]["人物编号"]=-1
		WAR.roles[i]["我方"]=true            --true 我方，false敌人
		WAR.roles[i]["坐标X"]=-1
		WAR.roles[i]["坐标Y"]=-1
		WAR.roles[i]["死亡"]=true
		WAR.roles[i]["人方向"]=-1
		WAR.roles[i]["贴图"]=-1
		WAR.roles[i]["贴图类型"]=0        --0 wmap中贴图，1 fight***中贴图
		WAR.roles[i]["轻功"]=0
		WAR.roles[i]["移动步数"]=0
		WAR.roles[i]["点数"]=0
		WAR.roles[i]["经验"]=0
		WAR.roles[i]["自动选择对手"]=-1     --自动战斗中每个人选择的战斗对手
	end

	WAR.num_roles=0               --战斗人物个数

	WAR.AutoFight=0               --我方自动战斗参数 0 手动，1 自动。

	WAR.CurID=-1                  --当前操作战斗人id

	WAR.ShowHead=0                --是否显示头像

	WAR.Effect=0              --效果，用来确认人物头上数字的颜色
	--2 杀生命 , 3 杀内力, 4 医疗 ， 5 用毒 ， 6 解毒

	WAR.EffectColor={}      ---定义人物头上数字的颜色
	WAR.EffectColor[2]=RGB(236, 200, 40)
	WAR.EffectColor[3]=RGB(112, 12, 112)
	WAR.EffectColor[4]=RGB(236, 200, 40)
	WAR.EffectColor[5]=RGB(96, 176, 64)
	WAR.EffectColor[6]=RGB(104, 192, 232)

	WAR.EffectXY=nil            --保存武功效果产生的坐标
	WAR.EffectXYNum=0          --坐标个数

end

--加载战斗数据
function WarLoad(warid)              --加载战斗数据
	WarSetGlobal()         --初始化战斗变量
	local data=Byte.create(CC.WarDataSize)      --读取战斗数据
	Byte.loadfile(data,CC.WarFile,warid*CC.WarDataSize,CC.WarDataSize)
	LoadData(WAR.Data,CC.WarData_S,data)
end

--战斗主函数
--warid  战斗编号
--isexp  输后是否有经验 0 没经验，1 有经验
--返回  true 战斗胜利，false 失败
function WarMain(warid,isexp)           --战斗主函数
	lib.Log(string.format("war start. warid=%d",warid))
	WarLoad(warid)
	WarSelectTeam()          --选择我方
	WarSelectEnemy()         --选择敌人

	CollectGarbage()
	lib.PicInit()
	--lib.AudioFadeOut(1000)
	lib.FadeOut(50)

	WarLoadMap(WAR.Data["地图"])       --加载战斗地图

	JY.game_status=GAME_STATUS_WARMAP

	--加载贴图文件
	lib.PicLoadFile(CC.WMAPPicFile[1],CC.WMAPPicFile[2],0)
	lib.PicLoadFile(CC.HeadPicFile[1],CC.HeadPicFile[2],1)
	if CC.LoadThingPic==1 then
		lib.PicLoadFile(CC.ThingPicFile[1],CC.ThingPicFile[2],2)
	end
	lib.PicLoadFile(CC.EffectFile[1],CC.EffectFile[2],3)

	PlayMIDI(WAR.Data["音乐"])

	local first=0            --第一次显示战斗标记
	local warStatus          --战斗状态

	WarPersonSort()    --战斗人按轻功排序

	for i=0,WAR.num_roles-1 do
		local pid=WAR.roles[i]["人物编号"]
		lib.PicLoadFile(string.format(CC.FightPicFile[1],JY.roles[pid]["头像代号"]),
		string.format(CC.FightPicFile[2],JY.roles[pid]["头像代号"]),
		4+i)
	end

	while true do             --战斗主循环

		for i=0,WAR.num_roles-1 do
			WAR.roles[i]["贴图"]=WarCalPersonPic(i)
		end
		for i=0,WAR.num_roles-1 do                ---计算各人的轻功，包括装备加成
			local id=WAR.roles[i]["人物编号"]
			local move=math.modf(WAR.roles[i]["轻功"]/15)-math.modf(JY.roles[id]["受伤程度"]/40)
			if move<0 then
				move=0
			end
			WAR.roles[i]["移动步数"]=move
		end

		WarSetPerson()     --设置战斗人物位置

		local p=0
		while p<WAR.num_roles do       --每回合战斗循环，每个人轮流战斗
			collectgarbage("step",0)
			WAR.Effect=0
			if WAR.AutoFight==1 then                 --我方自动战斗时读取键盘，看是否取消
				local keypress=lib.GetKey()
				if keypress==VK_SPACE or keypress==VK_RETURN then
					WAR.AutoFight=0
				end
			end


			if WAR.roles[p]["死亡"]==false then

				WAR.CurID=p

				if first==0 then              --第一次渐亮显示
					WarDrawMap(0)
					lib.FadeIn(50)
					first=1
				else
					--                    WarDrawMap(0)
					--WarShowHead()
					--                    UpdateScreen()
				end

				local r
				if WAR.roles[p]["我方"]==true then
					if WAR.AutoFight==0 then
						r=War_Manual()                  --手动战斗
					else
						r=War_Auto()                  --自动战斗
					end
				else
					r=War_Auto()                  --自动战斗
				end

				warStatus=War_isEnd()        --战斗是否结束？   0继续，1赢，2输

				if math.abs(r)==7 then         --等待
					p=p-1
				end

				if warStatus>0 then
					break
				end

			end
			p=p+1
		end

		if warStatus>0 then
			break
		end

		War_PersonLostLife()
	end

	local r

	WAR.ShowHead=0

	if warStatus==1 then
		DrawStrBoxWaitKey("战斗胜利",C_WHITE,CC.DefaultFontSize)
		r=true
	elseif warStatus==2 then
		DrawStrBoxWaitKey("战斗失败",C_WHITE,CC.DefaultFontSize)
		r=false
	end

	War_EndPersonData(isexp,warStatus)    --战斗后设置人物参数

	--lib.AudioFadeOut(1000)
	lib.FadeOut(50)

	if JY.scenes[JY.subscene]["进门音乐"]>=0 then
		PlayMIDI(JY.scenes[JY.subscene]["进门音乐"])
	else
		PlayMIDI(0)
	end

	CollectGarbage()
	lib.PicInit()
	lib.PicLoadFile(CC.SMAPPicFile[1],CC.SMAPPicFile[2],0)
	lib.PicLoadFile(CC.HeadPicFile[1],CC.HeadPicFile[2],1)
	if CC.LoadThingPic==1 then
		lib.PicLoadFile(CC.ThingPicFile[1],CC.ThingPicFile[2],2)
	end

	JY.game_status=GAME_STATUS_SCENE

	return r
end


function War_PersonLostLife()             --计算战斗后每回合由于中毒或受伤而掉血
	for i=0,WAR.num_roles-1 do
		local pid=WAR.roles[i]["人物编号"]
		if WAR.roles[i]["死亡"]==false then
			if JY.roles[pid]["受伤程度"]>0 then
				local dec=math.modf(JY.roles[pid]["受伤程度"]/20)
				AddPersonAttrib(pid,"生命",-dec)
			end
			if JY.roles[pid]["中毒程度"]>0 then
				local dec=math.modf(JY.roles[pid]["中毒程度"]/10)
				AddPersonAttrib(pid,"生命",-dec)
			end
			if JY.roles[pid]["生命"]<=0 then
				JY.roles[pid]["生命"]=1
			end
		end
	end
end


function War_EndPersonData(isexp,warStatus)            --战斗以后设置人物参数
	--敌方人员参数恢复
	for i=0,WAR.num_roles-1 do
		local pid=WAR.roles[i]["人物编号"]
		if WAR.roles[i]["我方"]==false then
			JY.roles[pid]["生命"]=JY.roles[pid]["生命最大值"]
			JY.roles[pid]["内力"]=JY.roles[pid]["内力最大值"]
			JY.roles[pid]["体力"]=CC.PersonAttribMax["体力"]
			JY.roles[pid]["受伤程度"]=0
			JY.roles[pid]["中毒程度"]=0
		end
	end

	--我方人员参数恢复，输赢都有
	for i=0,WAR.num_roles-1 do
		local pid=WAR.roles[i]["人物编号"]
		if WAR.roles[i]["我方"]==true then
			if JY.roles[pid]["生命"]<JY.roles[pid]["生命最大值"]/5 then
				JY.roles[pid]["生命"]=math.modf(JY.roles[pid]["生命最大值"]/5)
			end
			if JY.roles[pid]["体力"]<10 then
				JY.roles[pid]["体力"]=10 
			end
		end
	end

	if warStatus==2 and isexp==0 then  --输，没有经验，退出
		return 
	end

	local liveNum=0          --计算我方活着的人数
	for i=0,WAR.num_roles-1 do
		if WAR.roles[i]["我方"]==true and JY.roles[WAR.roles[i]["人物编号"]]["生命"]>0  then
			liveNum=liveNum+1
		end
	end

	--分配战斗经验---基本经验，战斗数据中的
	if warStatus==1 then     --赢了才有
		for i=0,WAR.num_roles-1 do
			local pid=WAR.roles[i]["人物编号"]
			if WAR.roles[i]["我方"]==true then
				if JY.roles[pid]["生命"]>0 then
					WAR.roles[i]["经验"]=WAR.roles[i]["经验"] + math.modf(WAR.Data["经验"]/liveNum)
				end
			end
		end
	end


	--每个人经验增加，以及升级
	for i=0,WAR.num_roles-1 do
		local pid=WAR.roles[i]["人物编号"]
		AddPersonAttrib(pid,"物品修炼点数",math.modf(WAR.roles[i]["经验"]*8/10))
		AddPersonAttrib(pid,"修炼点数",math.modf(WAR.roles[i]["经验"]*8/10))
		AddPersonAttrib(pid,"经验",WAR.roles[i]["经验"])

		if WAR.roles[i]["我方"]==true then

			DrawStrBoxWaitKey( string.format("%s 获得经验点数 %d",JY.roles[pid]["姓名"],WAR.roles[i]["经验"]),C_WHITE,CC.DefaultFontSize)

			local r=War_AddPersonLevel(pid)     --人物升级

			if r==true then
				DrawStrBoxWaitKey( string.format("%s 升级了",JY.roles[pid]["姓名"]),C_WHITE,CC.DefaultFontSize)
			end
		end

		War_PersonTrainBook(pid)            --修炼秘籍
		War_PersonTrainDrug(pid)            --修炼药品
	end
end

--人物是否升级
--pid 人id
--返回 true 升级，false不升级
function War_AddPersonLevel(pid)      --人物是否升级

	local tmplevel=JY.roles[pid]["等级"]
	if tmplevel>=CC.Level then     --级别到顶
		return false
	end

	if JY.roles[pid]["经验"]<CC.Exp[tmplevel] then     --经验不够升级
		return false
	end

	while true do          --判断可以升几级
		if tmplevel >= CC.Level then
			break
		end
		if JY.roles[pid]["经验"]>=CC.Exp[tmplevel] then
			tmplevel=tmplevel+1
		else
			break
		end
	end
	local leveladd=tmplevel-JY.roles[pid]["等级"]   --升级次数
	JY.roles[pid]["等级"]=JY.roles[pid]["等级"]+leveladd
	AddPersonAttrib(pid,"生命最大值", (JY.roles[pid]["生命增长"]+Random(3))*leveladd*3)
	JY.roles[pid]["生命"]=JY.roles[pid]["生命最大值"]
	JY.roles[pid]["体力"]=CC.PersonAttribMax["体力"]
	JY.roles[pid]["受伤程度"]=0
	JY.roles[pid]["中毒程度"]=0

	local cleveradd
	if JY.roles[pid]["资质"]<30 then
		cleveradd=2
	elseif JY.roles[pid]["资质"]<50 then
		cleveradd=3
	elseif JY.roles[pid]["资质"]<70 then
		cleveradd=4
	elseif JY.roles[pid]["资质"]<90 then
		cleveradd=5
	else
		cleveradd=6
	end
	cleveradd=Random(cleveradd)+1        --按照资质计算的增长点，越高则技能增加越多，而内力增加越少。
	AddPersonAttrib(pid,"内力最大值",  (9-cleveradd)*leveladd*4)   --聪明人不练内力
	JY.roles[pid]["内力"]=JY.roles[pid]["内力最大值"]

	AddPersonAttrib(pid,"攻击力",  cleveradd*leveladd)
	AddPersonAttrib(pid,"防御力",  cleveradd*leveladd)
	AddPersonAttrib(pid,"轻功",  cleveradd*leveladd)

	if JY.roles[pid]["医疗能力"]>=20 then
		AddPersonAttrib(pid,"医疗能力",  Random(3))
	end
	if JY.roles[pid]["用毒能力"]>=20 then
		AddPersonAttrib(pid,"用毒能力",  Random(3))
	end
	if JY.roles[pid]["解毒能力"]>=20 then
		AddPersonAttrib(pid,"解毒能力",  Random(3))
	end
	if JY.roles[pid]["拳掌功夫"]>=20 then
		AddPersonAttrib(pid,"拳掌功夫",  Random(3))
	end
	if JY.roles[pid]["御剑能力"]>=20 then
		AddPersonAttrib(pid,"御剑能力",  Random(3))
	end
	if JY.roles[pid]["耍刀技巧"]>=20 then
		AddPersonAttrib(pid,"耍刀技巧",  Random(3))
	end
	if JY.roles[pid]["暗器技巧"]>=20 then
		AddPersonAttrib(pid,"暗器技巧",  Random(3))
	end

	return true

end

--修炼秘籍
function War_PersonTrainBook(pid)           --战斗后修炼秘籍是否成功
	local p=JY.roles[pid]

	local thingid=p["修炼物品"]
	if thingid<0 then
		return 
	end

	local wugongid=JY.items[thingid]["练出武功"]

	local needpoint=TrainNeedExp(pid)      --计算修炼成功需要点数

	if p["修炼点数"]>=needpoint then   --修炼成功

		DrawStrBoxWaitKey( string.format("%s 修炼 %s 成功",p["姓名"],JY.items[thingid]["名称"]),C_WHITE,CC.DefaultFontSize)

		AddPersonAttrib(pid,"生命最大值",JY.items[thingid]["加生命最大值"])
		if JY.items[thingid]["改变内力性质"]==2 then
			p["内力性质"]=2
		end
		AddPersonAttrib(pid,"内力最大值",JY.items[thingid]["加内力最大值"])
		AddPersonAttrib(pid,"攻击力",JY.items[thingid]["加攻击力"])
		AddPersonAttrib(pid,"轻功",JY.items[thingid]["加轻功"])
		AddPersonAttrib(pid,"防御力",JY.items[thingid]["加防御力"])
		AddPersonAttrib(pid,"医疗能力",JY.items[thingid]["加医疗能力"])
		AddPersonAttrib(pid,"用毒能力",JY.items[thingid]["加用毒能力"])
		AddPersonAttrib(pid,"解毒能力",JY.items[thingid]["加解毒能力"])
		AddPersonAttrib(pid,"抗毒能力",JY.items[thingid]["加抗毒能力"])
		AddPersonAttrib(pid,"拳掌功夫",JY.items[thingid]["加拳掌功夫"])
		AddPersonAttrib(pid,"御剑能力",JY.items[thingid]["加御剑能力"])
		AddPersonAttrib(pid,"耍刀技巧",JY.items[thingid]["加耍刀技巧"])
		AddPersonAttrib(pid,"特殊兵器",JY.items[thingid]["加特殊兵器"])
		AddPersonAttrib(pid,"暗器技巧",JY.items[thingid]["加暗器技巧"])
		AddPersonAttrib(pid,"武学常识",JY.items[thingid]["加武学常识"])
		AddPersonAttrib(pid,"品德",JY.items[thingid]["加品德"])
		AddPersonAttrib(pid,"攻击带毒",JY.items[thingid]["加攻击带毒"])
		if JY.items[thingid]["加攻击次数"]==1 then
			p["左右互搏"]=1
		end

		p["修炼点数"]=0

		if wugongid>=0 then
			local oldwugong=0
			for i =1,10 do
				if p["武功" .. i]==wugongid then
					oldwugong=1
					p["武功等级" .. i]=p["武功等级" .. i]+100

					DrawStrBoxWaitKey(string.format("%s 升为第%s级",JY.kongfus[wugongid]["名称"],math.modf(p["武功等级" ..i]/100)+1),C_WHITE,CC.DefaultFontSize)

					break
				end
			end
			if oldwugong==0 then  --新的武功
				for i=1,10 do
					if p["武功" .. i]==0 then
						p["武功" .. i]=wugongid
						break
					end
				end
				---这里不考虑10个武功已满的时候如何增加新的武功
			end
		end
