end

--练出药品
function War_PersonTrainDrug(pid)         --战斗后是否修炼出物品
	local p=JY.roles[pid]

	local thingid=p["修炼物品"]
	if thingid<0 then
		return 
	end

	if JY.items[thingid]["练出物品需经验"] <=0 then          --不可以修炼出物品
		return 
	end

	local needpoint=(7-math.modf(p["资质"]/15))*JY.items[thingid]["练出物品需经验"]
	if p["物品修炼点数"]< needpoint then
		return 
	end

	local haveMaterial=0       --是否有需要的材料
	local MaterialNum=-1
	for i=1,CC.MyThingNum do
		if JY.base["物品" .. i]==JY.items[thingid]["需材料"] then
			haveMaterial=1
			MaterialNum=JY.base["物品数量" .. i]
			break
		end
	end

	if haveMaterial==1 then   --有材料
		local enough={}
		local canMake=0
		for i=1,5 do       --根据需要材料的数量，标记可以练出哪些物品
			if JY.items[thingid]["练出物品" .. i] >=0 and MaterialNum >= JY.items[thingid]["需要物品数量" .. i] then
				canMake=1
				enough[i]=1
			else
				enough[i]=0
			end
		end

		if canMake ==1 then    --可练出物品
			local makeID
			while true do      --随机选择练出的物品，必须是前面enough数组中标记可以练出的
				makeID=Random(5)+1
				if enough[makeID]==1 then
					break
				end
			end
			local newThingID=JY.items[thingid]["练出物品" .. makeID]

			DrawStrBoxWaitKey(string.format("%s 制造出 %s",p["姓名"],JY.items[newThingID]["名称"]),C_WHITE,CC.DefaultFontSize)

			if instruct_18(newThingID)==true then       --已经有物品
				instruct_32(newThingID,1)
			else
				instruct_32(newThingID,1+Random(3))
			end

			instruct_32(JY.items[thingid]["需材料"],-JY.items[thingid]["需要物品数量" .. makeID])
			p["物品修炼点数"]=0
		end
	end
end

--战斗是否结束
--返回：0 继续   1 赢    2 输
function War_isEnd()           --战斗是否结束

	for i=0,WAR.num_roles-1 do
		if JY.roles[WAR.roles[i]["人物编号"]]["生命"]<=0 then
			WAR.roles[i]["死亡"]=true
		end
	end
	WarSetPerson()     --设置战斗人物位置

	Cls()
	UpdateScreen()

	local myNum=0
	local EmenyNum=0
	for i=0,WAR.num_roles-1 do
		if WAR.roles[i]["死亡"]==false then
			if WAR.roles[i]["我方"]==true then
				myNum=1
			else
				EmenyNum=1
			end
		end
	end

	if EmenyNum==0 then
		return 1
	end
	if myNum==0 then
		return 2
	end
	return 0
end

--选择我方参战人
function WarSelectTeam()            --选择我方参战人
	WAR.num_roles=0

	for i=1,6 do
		local id=WAR.Data["自动选择参战人" .. i]
		if id>=0 then
			WAR.roles[WAR.num_roles]["人物编号"]=id
			WAR.roles[WAR.num_roles]["我方"]=true
			WAR.roles[WAR.num_roles]["坐标X"]=WAR.Data["我方X"  .. i]
			WAR.roles[WAR.num_roles]["坐标Y"]=WAR.Data["我方Y"  .. i]
			WAR.roles[WAR.num_roles]["死亡"]=false
			WAR.roles[WAR.num_roles]["人方向"]=2
			WAR.num_roles=WAR.num_roles+1
		end
	end
	if WAR.num_roles>0 then
		return 
	end

	for i=1,CC.TeamNum do                 --设置事先确定的参战人
		WAR.SelectPerson[i]=0
		local id=JY.base["队伍" .. i]
		if id>=0 then
			for j=1,6 do
				if WAR.Data["手动选择参战人" .. j]==id then
					WAR.SelectPerson[i]=1
				end
			end
		end
	end

	local menu={}
	for i=1, CC.TeamNum do
		menu[i]={"",WarSelectMenu,0}
		local id=JY.base["队伍" .. i]
		if id>=0 then
			menu[i][3]=1
			local s=JY.roles[id]["姓名"]
			if WAR.SelectPerson[i]==1 then
				menu[i][1]="*" .. s
			else
				menu[i][1]=" " .. s
			end
		end
	end

	menu[CC.TeamNum+1]={" 结束",nil,1}

	while true do
		Cls()
		local x=(CC.ScreenW-7*CC.DefaultFontSize-2*CC.MenuBorderPixel)/2
		DrawStrBox(x,10,"请选择参战人物",C_WHITE,CC.DefaultFontSize)
		local r=ShowVerticalMenu(menu,CC.TeamNum+1,0,x,10+CC.SingleLineHeight,0,0,1,0,CC.DefaultFontSize,C_ORANGE,C_WHITE)
		Cls()

		for i=1,6 do
			if WAR.SelectPerson[i]>0 then
				WAR.roles[WAR.num_roles]["人物编号"]=JY.base["队伍" ..i]
				WAR.roles[WAR.num_roles]["我方"]=true
				WAR.roles[WAR.num_roles]["坐标X"]=WAR.Data["我方X"  .. i]
				WAR.roles[WAR.num_roles]["坐标Y"]=WAR.Data["我方Y"  .. i]
				WAR.roles[WAR.num_roles]["死亡"]=false
				WAR.roles[WAR.num_roles]["人方向"]=2
				WAR.num_roles=WAR.num_roles+1
			end
		end
		if WAR.num_roles>0 then   --选择了我方参战人
			break
		end
	end
end


