function WarDrawMap(flag,v1,v2,v3)
	local x=WAR.roles[WAR.CurID]["坐标X"]
	local y=WAR.roles[WAR.CurID]["坐标Y"]

	if flag==0 then
		lib.DrawWarMap(0,x,y,0,0,-1)
	elseif flag==1 then
		if WAR.Data["地图"]==0 then     --雪地地图用黑色菱形
			lib.DrawWarMap(1,x,y,v1,v2,-1)
		else
			lib.DrawWarMap(2,x,y,v1,v2,-1)
		end
	elseif flag==2 then
		lib.DrawWarMap(3,x,y,0,0,-1)
	elseif flag==4 then
		lib.DrawWarMap(4,x,y,v1,v2,v3)
	end
	if WAR.ShowHead==1 then
		WarShowHead()
	end
end


function WarPersonSort()               --战斗人物按轻功排序
	for i=0,WAR.num_roles-1 do                ---计算各人的轻功，包括装备加成
		local id=WAR.roles[i]["人物编号"]
		local add=0
		if JY.roles[id]["武器"]>-1 then
			add=add+JY.items[JY.roles[id]["武器"]]["加轻功"]
		end
		if JY.roles[id]["防具"]>-1 then
			add=add+JY.items[JY.roles[id]["防具"]]["加轻功"]
		end
		WAR.roles[i]["轻功"]=JY.roles[id]["轻功"]+add
		local move=math.modf(WAR.roles[i]["轻功"]/15)-math.modf(JY.roles[id]["受伤程度"]/40)
		if move<0 then
			move=0
		end
		WAR.roles[i]["移动步数"]=move
	end

	--按轻功排序，用比较笨的方法
	for i=0,WAR.num_roles-2 do
		local maxid=i
		for j=i,WAR.num_roles-1 do
			if WAR.roles[j]["轻功"]>WAR.roles[maxid]["轻功"] then
				maxid=j
			end
		end
		WAR.roles[maxid],WAR.roles[i]=WAR.roles[i],WAR.roles[maxid]
	end
end

--设置战斗人物位置和贴图
function WarSetPerson()            --设置战斗人物位置
	CleanWarMap(2,-1)
	CleanWarMap(5,-1)

	for i=0,WAR.num_roles-1 do
		if WAR.roles[i]["死亡"]==false then
			SetWarMap(WAR.roles[i]["坐标X"],WAR.roles[i]["坐标Y"],2,i)
			SetWarMap(WAR.roles[i]["坐标X"],WAR.roles[i]["坐标Y"],5,WAR.roles[i]["贴图"])
		end
	end
end


function WarCalPersonPic(id)       --计算战斗人物贴图
