--计算可移动步数
--id 战斗人id，
--stepmax 最大步数，
--flag=0  移动，物品不能绕过，1 武功，用毒医疗等，不考虑挡路。
function War_CalMoveStep(id,stepmax,flag)                   --计算可移动步数

	CleanWarMap(3,255)           --第三层坐标用来设置移动，先都设为255，

	local x=WAR.roles[id]["坐标X"]
	local y=WAR.roles[id]["坐标Y"]

	local steparray={}     --用数组保存第n步的坐标。
	for i=0,stepmax do
		steparray[i]={}
		steparray[i].x={}
		steparray[i].y={}
	end

	SetWarMap(x,y,3,0)
	steparray[0].num=1
	steparray[0].x[1]=x
	steparray[0].y[1]=y
	for i=0,stepmax-1 do       --根据第0步的坐标找出第1步，然后继续找
		War_FindNextStep(steparray,i,flag)
		if steparray[i+1].num==0 then
			break
		end
	end

	return steparray

end


function War_FindNextStep(steparray,step,flag)      --设置下一步可移动的坐标
	local num=0
	local step1=step+1
	for i=1,steparray[step].num do
		local x=steparray[step].x[i]
		local y=steparray[step].y[i]
		if x+1<CC.WarWidth-1 then                        --当前步数的相邻格
			local v=GetWarMap(x+1,y,3)
			if v ==255 and War_CanMoveXY(x+1,y,flag)==true then
				num= num+1
				steparray[step1].x[num]=x+1
				steparray[step1].y[num]=y
				SetWarMap(x+1,y,3,step1)
			end
		end

		if x-1>0 then                        --当前步数的相邻格
			local v=GetWarMap(x-1,y,3)
			if v ==255 and War_CanMoveXY(x-1,y,flag)==true then
				num=num+1
				steparray[step1].x[num]=x-1
				steparray[step1].y[num]=y
				SetWarMap(x-1,y,3,step1)
			end
		end

		if y+1<CC.WarHeight-1 then                        --当前步数的相邻格
			local v=GetWarMap(x,y+1,3)
			if v ==255 and War_CanMoveXY(x,y+1,flag)==true then
				num= num+1
				steparray[step1].x[num]=x
				steparray[step1].y[num]=y+1
				SetWarMap(x,y+1,3,step1)
			end
		end

		if y-1>0 then                        --当前步数的相邻格
			local v=GetWarMap(x ,y-1,3)
			if v ==255 and War_CanMoveXY(x,y-1,flag)==true then
				num= num+1
				steparray[step1].x[num]=x 
				steparray[step1].y[num]=y-1
				SetWarMap(x ,y-1,3,step1)
			end
		end
	end
	steparray[step1].num=num

end

function War_CanMoveXY(x,y,flag)  --坐标是否可以通过，判断移动时使用
	if GetWarMap(x,y,1)>0 then    --第1层有物体
		return false
	end
	if flag==0 then
		if CC.WarWater[GetWarMap(x,y,0)]~=nil then          --水面，不可走
			return false
		end
		if GetWarMap(x,y,2)>=0 then    --有人
			return false
		end
	end
	return true
end

function War_SelectMove()              ---选择移动位置
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]
	local x=x0
	local y=y0

	while true do
		local x2=x
		local y2=y

		WarDrawMap(1,x,y)

		UpdateScreen()
		local key=WaitKey()
		if key==VK_UP then
			y2=y-1
		elseif key==VK_DOWN then
			y2=y+1
		elseif key==VK_LEFT then
			x2=x-1
		elseif key==VK_RIGHT then
			x2=x+1
		elseif key==VK_SPACE or key==VK_RETURN then
			return x,y
		elseif key==VK_ESCAPE then
			return nil
		end

		if GetWarMap(x2,y2,3)<128 then
			x=x2
			y=y2
		end
	end

end


function War_MovePerson(x,y)            --移动人物到位置x,y

	local movenum=GetWarMap(x,y,3)
	WAR.roles[WAR.CurID]["移动步数"]=WAR.roles[WAR.CurID]["移动步数"]-movenum    --可移动步数减小

	local movetable={}  --   记录每步移动
	for i=movenum,1,-1 do    --从目的位置反着找到初始位置，作为移动的次序
		movetable[i]={}
		movetable[i].x=x
		movetable[i].y=y
		if GetWarMap(x-1,y,3)==i-1 then
			x=x-1
			movetable[i].direct=1
		elseif GetWarMap(x+1,y,3)==i-1 then
			x=x+1
			movetable[i].direct=2
		elseif GetWarMap(x,y-1,3)==i-1 then
			y=y-1
			movetable[i].direct=3
		elseif GetWarMap(x,y+1,3)==i-1 then
			y=y+1
			movetable[i].direct=0
		end
	end

	for i=1,movenum do
		local t1=lib.GetTime()

		SetWarMap(WAR.roles[WAR.CurID]["坐标X"],WAR.roles[WAR.CurID]["坐标Y"],2,-1)
		SetWarMap(WAR.roles[WAR.CurID]["坐标X"],WAR.roles[WAR.CurID]["坐标Y"],5,-1)

		WAR.roles[WAR.CurID]["坐标X"]=movetable[i].x
		WAR.roles[WAR.CurID]["坐标Y"]=movetable[i].y
		WAR.roles[WAR.CurID]["人方向"]=movetable[i].direct
		WAR.roles[WAR.CurID]["贴图"]=WarCalPersonPic(WAR.CurID)

		SetWarMap(WAR.roles[WAR.CurID]["坐标X"],WAR.roles[WAR.CurID]["坐标Y"],2,WAR.CurID)
		SetWarMap(WAR.roles[WAR.CurID]["坐标X"],WAR.roles[WAR.CurID]["坐标Y"],5,WAR.roles[WAR.CurID]["贴图"])

		WarDrawMap(0)
		UpdateScreen()
		local t2=lib.GetTime()
		if i<movenum then
			if (t2-t1) < 2 * FRAME_INTERVAL then
				lib.Delay(2 * FRAME_INTERVAL - (t2 - t1))
			end
		end
	end

end


function War_FightMenu()              --执行攻击菜单
	local pid=WAR.roles[WAR.CurID]["人物编号"]

	local numwugong=0
	local menu={}
	for i=1,10 do
		local tmp=JY.roles[pid]["武功" .. i]
		if tmp>0 then
			menu[i]={JY.kongfus[tmp]["名称"],nil,1}
			if JY.kongfus[tmp]["消耗内力点数"] > JY.roles[pid]["内力"] then
				menu[i][3]=0
			end
			numwugong=numwugong+1
		end
	end

	if numwugong==0 then
		return 0
	end
	local r
	if numwugong==1 then
		r=1
	else
		r=ShowVerticalMenu(menu,numwugong,0,CC.MainSubMenuX,CC.MainSubMenuY,0,0,1,1,CC.DefaultFontSize,C_ORANGE,C_WHITE)
	end
	if r==0 then
		return 0
	end

	WAR.ShowHead=0
	local r2=War_Fight_Sub(WAR.CurID,r)
	WAR.ShowHead=1
	Cls()
	return r2
end

--执行战斗，自动和手动战斗都调用
function War_Fight_Sub(id,num_kongfus,x,y)          --执行战斗
	local pid=WAR.roles[id]["人物编号"]
	local kongfus=JY.roles[pid]["武功" .. num_kongfus]
	local level=math.modf(JY.roles[pid]["武功等级" .. num_kongfus]/100)+1

	CleanWarMap(4,0)

	local fightscope=JY.kongfus[kongfus]["攻击范围"]
	--在map4标注武功攻击效果
	if fightscope==0 then
		if War_FightSelectType0(kongfus,level,x,y)==false then
			return 0
		end
	elseif fightscope==1 then
		War_FightSelectType1(kongfus,level,x,y)

	elseif fightscope==2 then
		War_FightSelectType2(kongfus,level,x,y)

	elseif fightscope==3 then
		if War_FightSelectType3(kongfus,level,x,y)==false then
			return 0
		end
	end

	local fightnum=1
	if JY.roles[pid]["左右互搏"]==1 then
		fightnum=2
	end

	for k=1,fightnum  do         --如果左右互搏，则攻击两次
		for i=0,CC.WarWidth-1 do
			for j=0,CC.WarHeight-1 do
				local effect=GetWarMap(i,j,4)
				if effect>0 then              --攻击效果地方
					local emeny=GetWarMap(i,j,2)
					if emeny>=0 then          --有人
						if WAR.roles[WAR.CurID]["我方"] ~= WAR.roles[emeny]["我方"] then       --是敌人
							--只有点和面攻击可以杀内力。此时伤害类型有效
							if JY.kongfus[kongfus]["伤害类型"]==1 and (fightscope==0 or fightscope==3) then
								WAR.roles[emeny]["点数"]=-War_WugongHurtNeili(emeny,kongfus,level)
								SetWarMap(i,j,4,3)
								WAR.Effect=3
							else
								WAR.roles[emeny]["点数"]=-War_WugongHurtLife(emeny,kongfus,level)
								WAR.Effect=2
								SetWarMap(i,j,4,2)
							end
						end
					end
				end
			end
		end

		War_ShowFight(pid,kongfus,JY.kongfus[kongfus]["武功类型"],level,x,y,JY.kongfus[kongfus]["武功动画&音效"])

		for i=0,WAR.num_roles-1 do
			WAR.roles[i]["点数"]=0
		end

		WAR.roles[WAR.CurID]["经验"]=WAR.roles[WAR.CurID]["经验"]+2

		if JY.roles[pid]["武功等级" .. num_kongfus]<900 then
			JY.roles[pid]["武功等级" .. num_kongfus]=JY.roles[pid]["武功等级" .. num_kongfus]+Random(2)+1
		end

		if math.modf(JY.roles[pid]["武功等级" .. num_kongfus]/100)+1 ~= level then    --武功升级了
			level=math.modf(JY.roles[pid]["武功等级" .. num_kongfus]/100)+1
			DrawStrBox(-1,-1,string.format("%s 升为 %d 级",JY.kongfus[kongfus]["名称"],level),C_ORANGE,CC.DefaultFontSize)
			UpdateScreen()
			lib.Delay(500)
			Cls()
			UpdateScreen()
		end

		AddPersonAttrib(pid,"内力",-math.modf((level+1)/2)*JY.kongfus[kongfus]["消耗内力点数"])

	end

	AddPersonAttrib(pid,"体力",-3)

	return 1
end

--选择点攻击
--x1,y1 攻击点，如果为空则手工选择
function War_FightSelectType0(kongfus,level,x1,y1)          --选择点攻击
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]
	War_CalMoveStep(WAR.CurID,JY.kongfus[kongfus]["移动范围" .. level],1)

	if x1==nil and y1==nil then
		x1,y1=War_SelectMove()              --选择攻击对象
	end
	if x1 ==nil then
		--lib.GetKey()
		ClearKeyBuffer()
		Cls()
		return false
	end

	WAR.roles[WAR.CurID]["人方向"]=War_Direct(x0,y0,x1,y1)

	SetWarMap(x1,y1,4,1)

	WAR.EffectXY={}
	WAR.EffectXY[1]={x1,y1}
	WAR.EffectXY[2]={x1,y1}

end

--选择线攻击
--direct 攻击方向，为空则手工设置
function War_FightSelectType1(kongfus,level,x,y)            --选择线攻击
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]
	local direct

	if x==nil and y==nil  then
		direct =-1
		DrawStrBox(CC.MainSubMenuX,CC.MainSubMenuY,"请选择攻击方向",C_ORANGE,CC.DefaultFontSize)
		UpdateScreen()

		while true do           --选择方向
			local key=WaitKey()
			if key==VK_UP then
				direct=0
			elseif key==VK_DOWN then
				direct=3
			elseif key==VK_LEFT then
				direct=2
			elseif key==VK_RIGHT then
				direct=1
			end
			if direct>=0 then
				break
			end
		end

		Cls(CC.MainSubMenuX,CC.MainSubMenuY,CC.ScreenW,CC.ScreenH)
		UpdateScreen()
	else
		direct=War_Direct(x0,y0,x,y)
	end

	WAR.roles[WAR.CurID]["人方向"]=direct
	local move=JY.kongfus[kongfus]["移动范围" .. level]

	WAR.EffectXY={}

	for i=1,move do
		if direct==0 then
			SetWarMap(x0,y0-i,4,1)
		elseif direct==3 then
			SetWarMap(x0,y0+i,4,1)
		elseif direct==2 then
			SetWarMap(x0-i,y0,4,1)
		elseif direct==1 then
			SetWarMap(x0+i,y0,4,1)
		end
	end

	if direct==0 then
		WAR.EffectXY[1]={x0,y0-1}
		WAR.EffectXY[2]={x0,y0-move}
	elseif direct==3 then
		WAR.EffectXY[1]={x0,y0+1}
		WAR.EffectXY[2]={x0,y0+move}
	elseif direct==2 then
		WAR.EffectXY[1]={x0-1,y0}
		WAR.EffectXY[2]={x0-move,y0}
	elseif direct==1 then
		WAR.EffectXY[1]={x0+1,y0}
		WAR.EffectXY[2]={x0+move,y0}
	end

end

--选择十字攻击
function War_FightSelectType2(kongfus,level)                 --选择十字攻击
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]

	local move=JY.kongfus[kongfus]["移动范围" .. level]

	WAR.EffectXY={}

	for i=1,move do
		SetWarMap(x0,y0-i,4,1)
		SetWarMap(x0,y0+i,4,1)
		SetWarMap(x0-i,y0,4,1)
		SetWarMap(x0+i,y0,4,1)
	end

	WAR.EffectXY[1]={x0-move,y0}
	WAR.EffectXY[2]={x0+move,y0}

end

--选择面攻击
--x1,y1 攻击点，如果为空则手工选择
function War_FightSelectType3(kongfus,level,x1,y1)            --选择面攻击
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]
	War_CalMoveStep(WAR.CurID,JY.kongfus[kongfus]["移动范围" .. level],1)

	if x1==nil and y1==nil then
		x1,y1=War_SelectMove()              --选择攻击对象
	end

	if x1 ==nil then
		--lib.GetKey()
		ClearKeyBuffer()
		Cls()
		return false
	end

	WAR.roles[WAR.CurID]["人方向"]=War_Direct(x0,y0,x1,y1)

	local move=JY.kongfus[kongfus]["杀伤范围" .. level]

	WAR.EffectXY={}

	for i=-move,move do
		for j=-move,move do
			SetWarMap(x1+i,y1+j,4,1)
		end
	end

	WAR.EffectXY[1]={x1-2*move,y1}
	WAR.EffectXY[2]={x1+2*move,y1}
end

--计算人方向
--(x1,y1) 人位置     -(x2,y2) 目标位置
--返回： 方向
function War_Direct(x1,y1,x2,y2)             --计算人方向
	local x=x2-x1
	local y=y2-y1

	if math.abs(y)>math.abs(x) then
		if y>0 then
			return 3
		else
			return 0
		end
	else
		if x>0 then
			return 1
		else
			return 2
		end
	end
end


--显示战斗动画
--pid 人id
--kongfus  武功编号， 0 表示用毒解毒等，使用普通攻击效果
--wogongtype =0 医疗用毒解毒，1,2,3,4 武功类型  -1 暗器
--level 武功等级
--x,y 攻击坐标
--eft  武功动画效果id  eft.idx/grp中的效果

function War_ShowFight(pid,kongfus,wugongtype,level,x,y,eft)              --显示战斗动画

	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]

	local fightdelay,fightframe,sounddelay
	if wugongtype>=0 then
		fightdelay=JY.roles[pid]["出招动画延迟" .. wugongtype+1]
		fightframe=JY.roles[pid]["出招动画帧数" .. wugongtype+1]
		sounddelay=JY.roles[pid]["武功音效延迟" .. wugongtype+1]
	else            ---暗器，这些数据什么意思？？
		fightdelay=0
		fightframe=-1
		sounddelay=-1
	end

	local framenum=fightdelay+CC.Effect[eft]            --总帧数

	local startframe=0               --计算fignt***中当前出招起始帧
	if wugongtype>=0 then
		for i=0,wugongtype-1 do
			startframe=startframe+4*JY.roles[pid]["出招动画帧数" .. i+1]
		end
	end

	local starteft=0          --计算起始武功效果帧
	for i=0,eft-1 do
		starteft=starteft+CC.Effect[i]
	end

	WAR.roles[WAR.CurID]["贴图类型"]=0
	WAR.roles[WAR.CurID]["贴图"]=WarCalPersonPic(WAR.CurID)

	WarSetPerson()

	WarDrawMap(0)
	UpdateScreen()

	local fastdraw = 0

	--在显示动画前先加载贴图
	local oldpic=WAR.roles[WAR.CurID]["贴图"]/2
	local oldpic_type=0

	local oldeft=-1

	for i=0,framenum-1 do
		local tstart=lib.GetTime()
		local mytype
		if fightframe>0 then
			WAR.roles[WAR.CurID]["贴图类型"]=1
			mytype=4+WAR.CurID
			if i<fightframe then
				WAR.roles[WAR.CurID]["贴图"]=(startframe+WAR.roles[WAR.CurID]["人方向"]*fightframe+i)*2
			end
		else       --暗器，不使用fight中图像
			WAR.roles[WAR.CurID]["贴图类型"]=0
			WAR.roles[WAR.CurID]["贴图"]=WarCalPersonPic(WAR.CurID)
			mytype=0
		end

		if i==sounddelay then
			PlayWavAtk(JY.kongfus[kongfus]["出招音效"])
		end
		if i==fightdelay then
			PlayWavE(eft)
		end
		local pic=WAR.roles[WAR.CurID]["贴图"]/2
		if fastdraw==1 then
			local rr=ClipRect(Cal_PicClip(0,0,oldpic,oldpic_type,0,0,pic,mytype))
			if rr ~=nil then
				lib.SetClip(rr.x1,rr.y1,rr.x2,rr.y2)
			end
		else
			lib.SetClip(0,0,0,0)
		end
		oldpic=pic
		oldpic_type=mytype

		if i<fightdelay then   --只显示出招
			WarDrawMap(4,pic*2,mytype,-1)
		else		--同时显示武功效果
			starteft=starteft+1            --此处似乎是eft第一个数据有问题，应该是10，现为9，因此加1

			if fastdraw==1 then
				local clip1={}
				clip1=Cal_PicClip(WAR.EffectXY[1][1]-x0,WAR.EffectXY[1][2]-y0,oldeft,3,
				WAR.EffectXY[1][1]-x0,WAR.EffectXY[1][2]-y0,starteft,3)
				local clip2={}
				clip2=Cal_PicClip(WAR.EffectXY[2][1]-x0,WAR.EffectXY[2][2]-y0,oldeft,3,
				WAR.EffectXY[2][1]-x0,WAR.EffectXY[2][2]-y0,starteft,3)
				local clip=ClipRect(MergeRect(clip1,clip2))

				if clip ~=nil then
					local area=(clip.x2-clip.x1)*(clip.y2-clip.y1)          --计算脏矩形面积
					if area <CC.ScreenW*CC.ScreenH/2 then        --面积足够小，则更新脏矩形。
						WarDrawMap(4,pic*2,mytype,starteft*2)
						lib.SetClip(clip.x1,clip.y1,clip.x2,clip.y2)
						WarDrawMap(4,pic*2,mytype,starteft*2)
					else    --面积太大，直接重绘
						lib.SetClip(0,0,CC.ScreenW,CC.ScreenH)
						WarDrawMap(4,pic*2,mytype,starteft*2)
					end
				else
					WarDrawMap(4,pic*2,mytype,starteft*2)
				end
			else
				lib.SetClip(0,0,0,0)
				WarDrawMap(4,pic*2,mytype,starteft*2)
			end
			oldeft=starteft
		end

		UpdateScreen(fastdraw)
		lib.SetClip(0,0,0,0)

		local tend=lib.GetTime()
		if tend-tstart<1* FRAME_INTERVAL then
			lib.Delay(1* FRAME_INTERVAL -(tend-tstart))
		end

	end

	lib.SetClip(0,0,0,0)
	WAR.roles[WAR.CurID]["贴图类型"]=0
	WAR.roles[WAR.CurID]["贴图"]=WarCalPersonPic(WAR.CurID)
	WarSetPerson()
	WarDrawMap(0)

	UpdateScreen()
	lib.Delay(200)

	WarDrawMap(2)          --全黑显示命中人物
	UpdateScreen()
	lib.Delay(200)

	WarDrawMap(0)
	UpdateScreen()

	local HitXY={}               --记录命中点数的坐标
	local HitXYNum=0
	for i = 0, WAR.num_roles-1 do
		local x1=WAR.roles[i]["坐标X"]
		local y1=WAR.roles[i]["坐标Y"]
		if WAR.roles[i]["死亡"]==false then
			if GetWarMap(x1,y1,4)>1 then
				local n=WAR.roles[i]["点数"]
				HitXY[HitXYNum]={x1,y1,string.format("%+d",n)}
				HitXYNum=HitXYNum+1
			end
		end
	end

	if HitXYNum>0 then
		local clips={}                --计算命中点数clip
		for i=0,HitXYNum-1 do
			local dx=HitXY[i][1]-x0
			local dy=HitXY[i][2]-y0
			local ll=string.len(HitXY[i][3])
			local w=ll*CC.DefaultFontSize/2+1
			clips[i]={x1=CC.XScale*(dx-dy)+CC.ScreenW/2,
			y1=CC.YScale*(dx+dy)+CC.ScreenH/2,
			x2=CC.XScale*(dx-dy)+CC.ScreenW/2+w,
			y2=CC.YScale*(dx+dy)+CC.ScreenH/2+CC.DefaultFontSize+1 }
		end

		local clip=clips[0]

		for i=1,HitXYNum-1 do
			clip=MergeRect(clip,clips[i])
		end

		local area=(clip.x2-clip.x1)*(clip.y2-clip.y1)

		for i=1,15 do           --显示命中的点数
			local tstart=lib.GetTime()
			local y_off=i*2+65
			if fastdraw==1 and area <CC.ScreenW*CC.ScreenH/2 then
				local tmpclip={x1=clip.x1, y1=clip.y1-y_off, x2=clip.x2, y2=clip.y2-y_off}
				tmpclip=ClipRect(tmpclip)
				if tmpclip~=nil then
					lib.SetClip(tmpclip.x1,tmpclip.y1,tmpclip.x2,tmpclip.y2)
					WarDrawMap(0)
					for j=0,HitXYNum-1 do
						DrawText(clips[j].x1, clips[j].y1-y_off, HitXY[j][3],
						WAR.EffectColor[WAR.Effect],CC.DefaultFontSize)
					end
				end
			else    --面积太大，直接重绘
				lib.SetClip(0,0,CC.ScreenW,CC.ScreenH)
				WarDrawMap(0)
				for j=0,HitXYNum-1 do
					DrawText(clips[j].x1, clips[j].y1-y_off, HitXY[j][3],
					WAR.EffectColor[WAR.Effect],CC.DefaultFontSize)
				end
			end

			UpdateScreen()
			lib.SetClip(0,0,0,0)

			local tend=lib.GetTime()
			if (tend - tstart) < FRAME_INTERVAL then
				lib.Delay(FRAME_INTERVAL - (tend - tstart))
			end
		end
	end

	lib.SetClip(0,0,0,0)
	WarDrawMap(0)
	UpdateScreen()
end

--武功伤害生命
--enemyid 敌人战斗id，
--kongfus  我方使用武功
--返回：伤害点数
function War_WugongHurtLife(emenyid,kongfus,level)             --计算武功伤害生命
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local eid=WAR.roles[emenyid]["人物编号"]

	--计算武学常识
	local mywuxue=0
	local emenywuxue=0
	for i=0,WAR.num_roles-1 do
		local id =WAR.roles[i]["人物编号"]
		if WAR.roles[i]["死亡"]==false and JY.roles[id]["武学常识"]>80 then
			if WAR.roles[WAR.CurID]["我方"]==WAR.roles[i]["我方"] then
				mywuxue=mywuxue+JY.roles[id]["武学常识"]
			else
				emenywuxue=emenywuxue+JY.roles[id]["武学常识"]
			end
		end
	end

	--计算实际使用武功等级
	while true do
		if math.modf((level+1)/2)*JY.kongfus[kongfus]["消耗内力点数"] > JY.roles[pid]["内力"] then
			level=level-1
		else
			break
		end
	end

	if level<=0 then     --防止出现左右互博时第一次攻击完毕，第二次攻击没有内力的情况。
		level=1
	end

	--武功武器配合增加攻击力
	local fightnum=0
	for i,v in ipairs(CC.ExtraOffense) do
		if v[1]==JY.roles[pid]["武器"] and v[2]==kongfus then
			fightnum=v[3]
			break
		end
	end

	--计算攻击力
	fightnum=fightnum+(JY.roles[pid]["攻击力"]*3+JY.kongfus[kongfus]["攻击力" .. level ])/2

	if JY.roles[pid]["武器"]>=0 then
		fightnum=fightnum+JY.items[JY.roles[pid]["武器"]]["加攻击力"]
	end
	if JY.roles[pid]["防具"]>=0 then
		fightnum=fightnum+JY.items[JY.roles[pid]["防具"]]["加攻击力"]
	end
	fightnum=fightnum+mywuxue

	--计算防御力
	local defencenum=JY.roles[eid]["防御力"]
	if JY.roles[eid]["武器"]>=0 then
		defencenum=defencenum+JY.items[JY.roles[eid]["武器"]]["加防御力"]
	end
	if JY.roles[eid]["防具"]>=0 then
		defencenum=defencenum+JY.items[JY.roles[eid]["防具"]]["加防御力"]
	end
	defencenum= defencenum+ emenywuxue

	--计算实际伤害
	local hurt=(fightnum-3*defencenum)*2/3+Random(20)-Random(20)
	if hurt <0 then
		hurt=Random(10)+1
	end
	hurt=hurt+JY.roles[pid]["体力"]/15+JY.roles[eid]["受伤程度"]/20

	--考虑距离因素
	local offset=math.abs(WAR.roles[WAR.CurID]["坐标X"]-WAR.roles[emenyid]["坐标X"])+
	math.abs(WAR.roles[WAR.CurID]["坐标Y"]-WAR.roles[emenyid]["坐标Y"])

	if offset <10 then
		hurt=hurt*(100-(offset-1)*3)/100
	else
		hurt=hurt*2/3
	end

	hurt=math.modf(hurt)
	if hurt<=0 then
		hurt=Random(8)+1
	end

	JY.roles[eid]["生命"]=JY.roles[eid]["生命"]-hurt
	WAR.roles[WAR.CurID]["经验"]=WAR.roles[WAR.CurID]["经验"]+math.modf(hurt/5)

	if JY.roles[eid]["生命"]<0 then                 --打死敌人获得额外经验
		JY.roles[eid]["生命"]=0
		WAR.roles[WAR.CurID]["经验"]=WAR.roles[WAR.CurID]["经验"]+JY.roles[eid]["等级"]*10
	end

	AddPersonAttrib(eid,"受伤程度",math.modf(hurt/10))

	--敌人中毒点数
	local poisonnum=level*JY.kongfus[kongfus]["敌人中毒点数"]+JY.roles[pid]["攻击带毒"]

	if JY.roles[eid]["抗毒能力"]< poisonnum and JY.roles[eid]["抗毒能力"]<90 then
		AddPersonAttrib(eid,"中毒程度",math.modf(poisonnum/15))
	end

	return hurt
end

--武功伤害内力
--enemyid 敌人战斗id，
--kongfus  我方使用武功
--返回：伤害点数
function War_WugongHurtNeili(enemyid,kongfus,level)           --计算武功伤害内力
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local eid=WAR.roles[enemyid]["人物编号"]

	local addvalue=JY.kongfus[kongfus]["加内力" .. level]
	local decvalue=JY.kongfus[kongfus]["杀内力" .. level]

	if addvalue>0 then
		if math.modf(addvalue/2)>0 then
			AddPersonAttrib(pid,"内力最大值",Random(math.modf(addvalue/2)))
		end
		AddPersonAttrib(pid,"内力",math.abs(addvalue+Random(3)-Random(3)))
	end
	return -AddPersonAttrib(eid,"内力",-math.abs(decvalue+Random(3)-Random(3)))
end

