---执行医疗，解毒用毒
---flag=1 用毒， 2 解毒，3 医疗 4 暗器
---thingid 暗器物品id
function War_ExecuteMenu(flag,thingid)            ---执行医疗，解毒用毒暗器
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local step

	if flag==1 then
		step=math.modf(JY.roles[pid]["用毒能力"]/15)+1         --用毒步数
	elseif flag==2 then
		step=math.modf(JY.roles[pid]["解毒能力"]/15)+1         --解毒步数
	elseif flag==3 then
		step=math.modf(JY.roles[pid]["医疗能力"]/15)+1         --医疗步数
	elseif flag==4 then
		step=math.modf(JY.roles[pid]["暗器技巧"]/15)+1         --暗器步数
	end

	War_CalMoveStep(WAR.CurID,step,1)

	local x1,y1=War_SelectMove()              --选择攻击对象

	if x1 ==nil then
		--lib.GetKey()
		ClearKeyBuffer()
		Cls()
		return 0
	else
		return War_ExecuteMenu_Sub(x1,y1,flag,thingid)
	end
end


function War_ExecuteMenu_Sub(x1,y1,flag,thingid)     ---执行医疗，解毒用毒暗器的子函数，自动医疗也可调用
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]

	CleanWarMap(4,0)

	WAR.roles[WAR.CurID]["人方向"]=War_Direct(x0,y0,x1,y1)

	SetWarMap(x1,y1,4,1)

	local emeny=GetWarMap(x1,y1,2)
	if emeny>=0 then          --有人
		if flag==1 then
			if WAR.roles[WAR.CurID]["我方"] ~= WAR.roles[emeny]["我方"] then       --是敌人
				WAR.roles[emeny]["点数"]=War_PoisonHurt(pid,WAR.roles[emeny]["人物编号"])
				SetWarMap(x1,y1,4,5)
				WAR.Effect=5
			end
		elseif flag==2 then
			if WAR.roles[WAR.CurID]["我方"] == WAR.roles[emeny]["我方"] then       --不是敌人
				WAR.roles[emeny]["点数"]=ExecDecPoison(pid,WAR.roles[emeny]["人物编号"])
				SetWarMap(x1,y1,4,6)
				WAR.Effect=6
			end
		elseif flag==3 then
			if WAR.roles[WAR.CurID]["我方"] == WAR.roles[emeny]["我方"] then       --不是敌人
				WAR.roles[emeny]["点数"]=DoHeal(pid,WAR.roles[emeny]["人物编号"])
				SetWarMap(x1,y1,4,4)
				WAR.Effect=4
			end
		elseif flag==4 then
			if WAR.roles[WAR.CurID]["我方"] ~= WAR.roles[emeny]["我方"] then       --是敌人
				WAR.roles[emeny]["点数"]=War_AnqiHurt(pid,WAR.roles[emeny]["人物编号"],thingid)
				SetWarMap(x1,y1,4,2)
				WAR.Effect=2
			end
		end

	end

	WAR.EffectXY={}
	WAR.EffectXY[1]={x1,y1}
	WAR.EffectXY[2]={x1,y1}

	if flag==1 then
		War_ShowFight(pid,0,0,0,x1,y1,30)
	elseif flag==2 then
		War_ShowFight(pid,0,0,0,x1,y1,36)
	elseif flag==3 then
		War_ShowFight(pid,0,0,0,x1,y1,0)
	elseif flag==4 then
		if emeny>=0 then
			War_ShowFight(pid,0,-1,0,x1,y1,JY.items[thingid]["暗器动画编号"])
		end
	end

	for i=0,WAR.num_roles-1 do
		WAR.roles[i]["点数"]=0
	end
	if flag==4 then
		if emeny>=0 then
			instruct_32(thingid,-1)            --物品数量减少
			return 1
		else
			return 0                   --暗器打空，则等于没有打
		end
	else
		WAR.roles[WAR.CurID]["经验"]=WAR.roles[WAR.CurID]["经验"]+1
		AddPersonAttrib(pid,"体力",-2)
	end

	return 1

end


