function War_AnqiHurt(pid,emenyid,thingid)         --计算暗器伤害
	local num
	if JY.roles[emenyid]["受伤程度"]==0 then
		num=JY.items[thingid]["加生命"]/4-Random(5)
	elseif JY.roles[emenyid]["受伤程度"]<=33 then
		num=JY.items[thingid]["加生命"]/3-Random(5)
	elseif JY.roles[emenyid]["受伤程度"]<=66 then
		num=JY.items[thingid]["加生命"]/2-Random(5)
	else
		num=JY.items[thingid]["加生命"]/2-Random(5)
	end

	num=math.modf((num-JY.roles[pid]["暗器技巧"]*2)/3)
	AddPersonAttrib(emenyid,"受伤程度",math.modf(-num/4))      --此处的num为负值

	local r=AddPersonAttrib(emenyid,"生命",math.modf(num))

	if JY.items[thingid]["加中毒解毒"]>0 then
		num=math.modf((JY.items[thingid]["加中毒解毒"]+JY.roles[pid]["暗器技巧"])/2)
		num=num-JY.roles[emenyid]["抗毒能力"]
		num=Clamp(num,0,CC.PersonAttribMax["用毒能力"])
		AddPersonAttrib(emenyid,"中毒程度",num)
	end
	return r
end

