function War_Auto()             --自动战斗主函数

	WAR.ShowHead=1
	WarDrawMap(0)
	UpdateScreen()
	lib.Delay(CC.WarAutoDelay)
	WAR.ShowHead=0

	if CC.AutoWarShowHead==1 then
		WAR.ShowHead=1
	end

	local autotype=War_Think()         --思考如何战斗

	if autotype==0 then  --休息
		War_AutoEscape()  --先跑开
		War_RestMenu()
	elseif autotype==1 then
		War_AutoFight()      --自动战斗
	elseif autotype==2 then    --吃药加生命
		War_AutoEscape()
		War_AutoEatDrug(2)
	elseif autotype==3 then    --吃药加内力
		War_AutoEscape()
		War_AutoEatDrug(3)
	elseif autotype==4 then    --吃药加体力
		War_AutoEscape()
		War_AutoEatDrug(4)
	elseif autotype==5 then    --自己医疗
		War_AutoEscape()
		War_AutoDoctor()
	elseif autotype==6 then    --吃药解毒
		War_AutoEscape()
		War_AutoEatDrug(6)
	end

	return 0
end

--思考如何战斗
--返回：0 休息， 1 战斗，2 使用物品增加生命， 3 使用物品增加内力 4 吃药加体力， 5 医疗
--     6 使用物品解毒

function War_Think()           --思考如何战斗
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local r=-1         --考虑的结果

	if JY.roles[pid]["体力"] <10 then         --休息
		r=War_ThinkDrug(4)
		if r>=0 then
			return r
		end
		return 0
	end

	if JY.roles[pid]["生命"]<20 or JY.roles[pid]["受伤程度"]>50 then
		r=War_ThinkDrug(2)       --考虑增加生命
		if r>=0 then
			return r
		end
	end

	local rate=-1         --增加生命的百分比
	if JY.roles[pid]["生命"] <JY.roles[pid]["生命最大值"] /5 then
		rate=90
	elseif JY.roles[pid]["生命"] <JY.roles[pid]["生命最大值"] /4 then
		rate=70
	elseif JY.roles[pid]["生命"] <JY.roles[pid]["生命最大值"] /3 then
		rate=50
	elseif JY.roles[pid]["生命"] <JY.roles[pid]["生命最大值"] /2 then
		rate=25
	end

	if Random(100)<rate then
		r=War_ThinkDrug(2)       --考虑增加生命
		if r>=0 then
			return r
		else             --没有增加生命的药，考虑是否自己医疗
			r=War_ThinkDoctor()
			if r>=0 then
				return r
			end
		end
	end

	rate=-1         --增加内力的百分比
	if JY.roles[pid]["内力"] <JY.roles[pid]["内力最大值"] /5 then
		rate=75
	elseif JY.roles[pid]["内力"] <JY.roles[pid]["内力最大值"] /4 then
		rate=50
	end

	if Random(100)<rate then
		r=War_ThinkDrug(3)       --考虑增加内力
		if r>=0 then
			return r
		end
	end


	rate=-1         --解毒的百分比
	if JY.roles[pid]["中毒程度"] > CC.PersonAttribMax["中毒程度"] *3/4 then
		rate=60
	elseif JY.roles[pid]["中毒程度"] >CC.PersonAttribMax["中毒程度"] /2 then
		rate=30
	end

	if Random(100)<rate then
		r=War_ThinkDrug(6)       --考虑解毒
		if r>=0 then
			return r
		end
	end

	local minNeili=War_GetMinNeiLi(pid)     --所有武功的最小内力

	if JY.roles[pid]["内力"]>=minNeili then
		r=1
	else
		r=0
	end

	return r
end

--能否吃药增加参数
--flag=2 生命，3内力；4体力  6 解毒
function War_ThinkDrug(flag)             --能否吃药增加参数
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local str
	local r=-1

	if flag==2 then
		str="加生命"
	elseif flag==3 then
		str="加内力"
	elseif flag==4 then
		str="加体力"
	elseif flag==6 then
		str="加中毒解毒"
	else
		return r
	end

	local function Get_Add(thingid)    --定义局部函数。取得物品thingid增加的值
		if flag==6 then
			return -JY.items[thingid][str]   --解毒为负值
		else
			return JY.items[thingid][str]
		end
	end

	if WAR.roles[WAR.CurID]["我方"]==true then
		for i =1, CC.MyThingNum do
			local thingid=JY.base["物品" ..i]
			if thingid>=0 then
				if JY.items[thingid]["类型"]==3 and Get_Add(thingid)>0 then
					r=flag                     ---有增加生命的药，则动作：使用物品加生命
					break
				end
			end
		end
	else
		for i =1, 4 do
			local thingid=JY.roles[pid]["携带物品" ..i]
			if thingid>=0 then
				if JY.items[thingid]["类型"]==3 and Get_Add(thingid)>0  then
					r=flag                     ---有增加生命的药，则动作：使用物品加生命
					break
				end
			end
		end
	end

	return r

end


--考虑是否自己医疗
function War_ThinkDoctor()          --考虑是否给自己医疗
	local pid=WAR.roles[WAR.CurID]["人物编号"]

	if JY.roles[pid]["体力"]<50 or JY.roles[pid]["医疗能力"]<20 then
		return -1
	end

	if JY.roles[pid]["受伤程度"]>JY.roles[pid]["医疗能力"]+20 then
		return -1
	end

	local rate = -1
	local v=JY.roles[pid]["生命最大值"]-JY.roles[pid]["生命"]
	if JY.roles[pid]["医疗能力"] < v/4 then
		rate=30
	elseif JY.roles[pid]["医疗能力"] < v/3 then
		rate=50
	elseif JY.roles[pid]["医疗能力"] < v/2 then
		rate=70
	else
		rate=90
	end

	if Random(100) <rate then
		return 5
	end

	return -1
end

---自动战斗
function War_AutoFight()             ---执行自动战斗

	local num_kongfus=War_AutoSelectWugong()    --选择武功

	if num_kongfus <=0 then --没有选择到武功，休息
		War_AutoEscape()
		War_RestMenu()
		return
	end

	local r=War_AutoMove(num_kongfus)         -- 往敌人方向移动
	if r==1 then   --如果在攻击范围
		War_AutoExecuteFight(num_kongfus)     --攻击
	else
		War_RestMenu()           --休息
	end
end


function War_AutoSelectWugong()           --自动选择合适的武功
	local pid=WAR.roles[WAR.CurID]["人物编号"]

	local probability={}       --每种武功选择的概率

	local num_kongfus=10         --缺省10种武功
	for i =1, 10 do             --计算每种可选择武功的总攻击力
		local wugongid=JY.roles[pid]["武功" .. i]
		if wugongid>0 then
			--选择杀生命的武功，必须消耗内力比现有内力小，起码可以发出一级的武功。
			if JY.kongfus[wugongid]["伤害类型"]==0 then
				if JY.kongfus[wugongid]["消耗内力点数"]<=JY.roles[pid]["内力"] then
					local level=math.modf(JY.roles[pid]["武功等级" .. i]/100)+1
					--总攻击力即为概率
					probability[i]=(JY.roles[pid]["攻击力"]*3+JY.kongfus[wugongid]["攻击力" .. level ])/2
				else
					probability[i]=0
				end
			else            --杀内力的武功
				probability[i]=10  --很小的概率选择杀内力
			end
		else
			num_kongfus=i-1
			break
		end
	end

	local maxoffense=0       --计算最大攻击力
	for i =1, num_kongfus do
		if  probability[i]>maxoffense then
			maxoffense=probability[i]
		end
	end

	local mynum=0             --计算我方和敌人个数
	local enemynum=0
	for i=0, WAR.num_roles-1 do
		if WAR.roles[i]["死亡"]==false then
			if WAR.roles[i]["我方"]==WAR.roles[WAR.CurID]["我方"] then
				mynum=mynum+1
			else
				enemynum=enemynum+1
			end
		end
	end

	local factor=0       --敌人人数影响因子，敌人多则对线面等攻击多人武功的选择概率增加
	if enemynum>mynum then
		factor=2
	else
		factor=1
	end

	for i =1, num_kongfus do       --考虑其他概率效果
		local wugongid=JY.roles[pid]["武功" .. i]
		if probability[i]>0 then
			if probability[i]<maxoffense/2 then       --去掉攻击力小的武功
				probability[i]=0
			end
			local extranum=0           --武功武器配合的攻击力
			for j,v in ipairs(CC.ExtraOffense) do
				if v[1]==JY.roles[pid]["武器"] and v[2]==wugongid then
					extranum=v[3]
					break
				end
			end
			local level=math.modf(JY.roles[pid]["武功等级" .. i]/100)+1
			probability[i]=probability[i]+JY.kongfus[wugongid]["攻击范围"]*factor*JY.kongfus[wugongid]["杀伤范围" ..level]*20
		end
	end

	local s={}           --按照概率依次累加
	local maxnum=0
	for i=1,num_kongfus do
		s[i]=maxnum
		maxnum=maxnum+probability[i]
	end
	s[num_kongfus+1]=maxnum

	if maxnum==0 then    --没有可以选择的武功
		return -1
	end

	local v=Random(maxnum)            --产生随机数
	local selectid=0
	for i=1,num_kongfus do            --根据产生的随机数，寻找落在哪个武功区间
		if v>=s[i] and v< s[i+1] then
			selectid=i
			break
		end
	end

	return selectid
end


function War_AutoSelectEnemy()             --选择战斗对手
	local enemyid=War_AutoSelectEnemy_near()
	WAR.roles[WAR.CurID]["自动选择对手"]=enemyid
	return enemyid
end


function War_AutoSelectEnemy_near()              --选择最近对手

	War_CalMoveStep(WAR.CurID,100,1)           --标记每个位置的步数

	local maxDest=math.huge
	local nearid=-1
	for i=0,WAR.num_roles-1 do           --查找最近步数的敌人
		if WAR.roles[WAR.CurID]["我方"] ~=WAR.roles[i]["我方"] then
			if WAR.roles[i]["死亡"]==false then
				local step=GetWarMap(WAR.roles[i]["坐标X"],WAR.roles[i]["坐标Y"],3)
				if step<maxDest then
					nearid=i
					maxDest=step
				end
			end
		end
	end
	return nearid
end

--自动往敌人方向移动
--人物武功编号，不是武功id
--返回 1=可以攻击敌人， 0 不能攻击
function War_AutoMove(num_kongfus)              --自动往敌人方向移动
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local wugongid=JY.roles[pid]["武功"  ..num_kongfus]
	local level=math.modf(JY.roles[pid]["武功等级".. num_kongfus]/100)+1

	local wugongtype=JY.kongfus[wugongid]["攻击范围"]
	local movescope=JY.kongfus[wugongid]["移动范围" ..level]
	local fightscope=JY.kongfus[wugongid]["杀伤范围" ..level]
	local scope=movescope+fightscope


	local x,y
	local move=128
	local maxenemy=0

	local movestep=War_CalMoveStep(WAR.CurID,WAR.roles[WAR.CurID]["移动步数"],0)   --计算移动步数

	War_AutoCalMaxEnemyMap(wugongid,level)  --计算该武功各个坐标可以攻击到敌人的个数

	for i=0,WAR.roles[WAR.CurID]["移动步数"] do
		local step_num=movestep[i].num 
		if step_num==0 then
			break
		end
		for j=1,step_num do
			local xx=movestep[i].x[j]
			local yy=movestep[i].y[j]

			local num=0
			if wugongtype==0 or wugongtype==2 or wugongtype==3 then
				num=GetWarMap(xx,yy,4)      --计算这个位置可以攻击到的最多敌人个数
			elseif wugongtype==1  then
				local v=GetWarMap(xx,yy,4)      --计算这个位置可以攻击到的最多敌人个数
				if v>0 then
					num=War_AutoCalMaxEnemy(xx,yy,wugongid,level)
				end
			end
			if num>maxenemy then
				maxenemy=num
				x=xx
				y=yy
				move=i
			elseif num==maxenemy and num>0 then
				if Random(3)==0 then
					maxenemy=num
					x=xx
					y=yy
					move=i
				end
			end
		end
	end

	if maxenemy>0 then
		War_CalMoveStep(WAR.CurID,WAR.roles[WAR.CurID]["移动步数"],0)   --重新计算移动步数
		War_MovePerson(x,y)    --移动到相应的位置
		return 1
	else   --任何移动都直接攻击不到敌人，寻找一条可以移动到攻击到敌人位置的路线
		x,y=War_GetCanFightEnemyXY(scope)       --计算可以攻击到敌人的最近位置

		local minDest=math.huge
		if x==nil then   --无法走到可以攻击敌人的地方，可能敌人被围住，或者被敌人围住。
			local enemyid=War_AutoSelectEnemy()   --选择最近敌人

			War_CalMoveStep(WAR.CurID,100,0)   --计算移动步数 假设最大100步

			for i=0,CC.WarWidth-1 do
				for j=0,CC.WarHeight-1 do
					local dest=GetWarMap(i,j,3)
					if dest <128 then
						local dx=math.abs(i-WAR.roles[enemyid]["坐标X"])
						local dy=math.abs(j-WAR.roles[enemyid]["坐标Y"])
						if minDest>(dx+dy) then        --此时x,y是距离敌人的最短路径，虽然可能被围住
							minDest=dx+dy
							x=i
							y=j
						elseif minDest==(dx+dy) then
							if Random(2)==0 then
								x=i
								y=j
							end
						end
					end
				end
			end
		else
			minDest=0        --可以走到
		end

		if minDest<math.huge then   --有路可走
			while true do    --从目的位置反着找到可以移动的位置，作为移动的次序
				local i=GetWarMap(x,y,3)
				if i<=WAR.roles[WAR.CurID]["移动步数"] then
					break
				end

				if GetWarMap(x-1,y,3)==i-1 then
					x=x-1
				elseif GetWarMap(x+1,y,3)==i-1 then
					x=x+1
				elseif GetWarMap(x,y-1,3)==i-1 then
					y=y-1
				elseif GetWarMap(x,y+1,3)==i-1 then
					y=y+1
				end
			end
			War_MovePerson(x,y)    --移动到相应的位置
		end
	end

	return 0
end

--得到可以走到攻击到敌人的最近位置。
--scope可以攻击的范围
--返回 x,y。如果无法走到攻击位置，返回空


function War_GetCanFightEnemyXY(scope)             --得到可以走到攻击到敌人的最近位置

	local minStep=math.huge
	local newx,newy

	War_CalMoveStep(WAR.CurID,100,0)   --计算移动步数 假设最大100步
	for x=0,CC.WarWidth-1 do
		for y=0,CC.WarHeight-1 do
			if GetWarMap(x,y,4)>0 then    --这个位置可以攻击到敌人
				local step=GetWarMap(x,y,3)
				if step<128 then
					if minStep>step then
						minStep=step
						newx=x
						newy=y
					elseif minStep==step then
						if Random(2)==0 then
							newx=x
							newy=y
						end
					end
				end
			end
		end
	end

	if minStep<math.huge then
		return newx,newy
	end
end


function War_AutoCalMaxEnemyMap(wugongid,level)       --计算地图上每个位置可以攻击的敌人数目

	local wugongtype=JY.kongfus[wugongid]["攻击范围"]
	local movescope=JY.kongfus[wugongid]["移动范围" ..level]
	local fightscope=JY.kongfus[wugongid]["杀伤范围" ..level]

	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]

	CleanWarMap(4,0)    --用level 4地图表示哪些位置可以攻击到敌人

	----点攻击和面攻击, 每个坐标可以攻击的敌人个数（显然只能为0和1）
	---这里面攻击和点攻击一样处理，会导致面攻击可能不能攻击到最多的敌人，但是这样速度快
	if wugongtype==0 or wugongtype==3 then
		for n=0,WAR.num_roles-1 do
			if n~=WAR.CurID and WAR.roles[n]["死亡"]==false and
				WAR.roles[n]["我方"] ~=WAR.roles[WAR.CurID]["我方"] then   --敌人
				local xx=WAR.roles[n]["坐标X"]
				local yy=WAR.roles[n]["坐标Y"]
				local movestep=War_CalMoveStep(n,movescope,1)   --计算武功移动步数
				for i=1,movescope do
					local step_num=movestep[i].num 
					if step_num==0 then
						break
					end
					for j=1,step_num do
						SetWarMap(movestep[i].x[j],movestep[i].y[j],4,1)  --标记武功移动的地方，即为可攻击到敌人之处
					end
				end
			end
		end
		--线攻击和十字 记录每个的点可以攻击到敌人的个数。对线攻击，数组并不准确，需要进一步核实。
	elseif wugongtype==1 or wugongtype==2  then
		for n=0,WAR.num_roles-1 do
			if n~=WAR.CurID and WAR.roles[n]["死亡"]==false and
				WAR.roles[n]["我方"] ~=WAR.roles[WAR.CurID]["我方"] then   --敌人
				local xx=WAR.roles[n]["坐标X"]
				local yy=WAR.roles[n]["坐标Y"]
				for direct=0,3 do
					for i=1,movescope do
						local xnew=xx+CC.DirectX[direct+1]*i
						local ynew=yy+CC.DirectY[direct+1]*i
						if xnew>=0 and xnew<CC.WarWidth and ynew>=0 and ynew<CC.WarHeight then
							local v=GetWarMap(xnew,ynew,4)
							SetWarMap(xnew,ynew,4,v+1)
						end
					end
				end
			end
		end

	end

end


function War_AutoCalMaxEnemy(x,y,wugongid,level)       --计算从(x,y)开始攻击最多能够击中几个敌人

	local wugongtype=JY.kongfus[wugongid]["攻击范围"]
	local movescope=JY.kongfus[wugongid]["移动范围" ..level]
	local fightscope=JY.kongfus[wugongid]["杀伤范围" ..level]

	local maxnum=0
	local xmax,ymax

	if wugongtype==0 or wugongtype==3 then

		local movestep=War_CalMoveStep(WAR.CurID,movescope,1)   --计算武功移动步数
		for i=1,movescope do
			local step_num=movestep[i].num 
			if step_num==0 then
				break
			end
			for j=1,step_num do
				local xx=movestep[i].x[j]
				local yy=movestep[i].y[j]
				local enemynum=0

				for n=0,WAR.num_roles-1 do   --计算武功攻击范围内的敌人个数
					if n~=WAR.CurID and WAR.roles[n]["死亡"]==false and
						WAR.roles[n]["我方"] ~=WAR.roles[WAR.CurID]["我方"] then
						local x=math.abs(WAR.roles[n]["坐标X"]-xx)
						local y=math.abs(WAR.roles[n]["坐标Y"]-yy)
						if x<=fightscope and y <=fightscope then
							enemynum=enemynum+1
						end
					end
				end

				if enemynum>maxnum then        --记录最多敌人和位置
					maxnum=enemynum
					xmax=xx
					ymax=yy
				end
			end
		end

	elseif wugongtype==1 then    --线攻击
		for direct=0,3 do           -- 对每个方向循环，找出敌人最多的
			local enemynum=0
			for i=1,movescope do
				local xnew=x+CC.DirectX[direct+1]*i
				local ynew=y+CC.DirectY[direct+1]*i

				if xnew>=0 and xnew<CC.WarWidth and ynew>=0 and ynew<CC.WarHeight then
					local id=GetWarMap(xnew,ynew,2)
					if id>=0 then
						if WAR.roles[WAR.CurID]["我方"] ~= WAR.roles[id]["我方"] then
							enemynum=enemynum+1                  --武功攻击范围内的敌人个数
						end
					end
				end
			end
			if enemynum>maxnum then        --记录最多敌人和位置
				maxnum=enemynum
				xmax=x+CC.DirectX[direct+1]       --线攻击记录一个代表方向的坐标
				ymax=y+CC.DirectY[direct+1]
			end
		end

	elseif wugongtype==2 then --十字攻击
		local enemynum=0
		for direct=0,3 do           -- 对每个方向循环
			for i=1,movescope do
				local xnew=x+CC.DirectX[direct+1]*i
				local ynew=y+CC.DirectY[direct+1]*i
				if xnew>=0 and xnew<CC.WarWidth and ynew>=0 and ynew<CC.WarHeight then
					local id=GetWarMap(xnew,ynew,2)
					if id>=0 then
						if WAR.roles[WAR.CurID]["我方"] ~= WAR.roles[id]["我方"] then
							enemynum=enemynum+1                  --武功攻击范围内的敌人个数
						end
					end
				end
			end
		end
		if enemynum>0 then
			maxnum=enemynum
			xmax=x
			ymax=y
		end
	end
	return maxnum,xmax,ymax
end

--自动执行战斗，此时的位置一定可以打到敌人
function War_AutoExecuteFight(num_kongfus)            --自动执行战斗，显示攻击动画
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local x0=WAR.roles[WAR.CurID]["坐标X"]
	local y0=WAR.roles[WAR.CurID]["坐标Y"]
	local wugongid=JY.roles[pid]["武功"  ..num_kongfus]
	local level=math.modf(JY.roles[pid]["武功等级".. num_kongfus]/100)+1

	local maxnum,x,y=War_AutoCalMaxEnemy(x0,y0,wugongid,level)

	if x ~= nil then
		War_Fight_Sub(WAR.CurID,num_kongfus,x,y)
	end

end

--逃跑
function War_AutoEscape()                --逃跑
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	if JY.roles[pid]["体力"]<=5  then
		return
	end

	local maxDest=0
	local x,y

	War_CalMoveStep(WAR.CurID,WAR.roles[WAR.CurID]["移动步数"],0)   --计算移动步数

	for i=0,CC.WarWidth-1 do
		for j=0,CC.WarHeight-1 do
			if GetWarMap(i,j,3)<128 then
				local minDest=math.huge
				for k=0,WAR.num_roles-1 do
					if WAR.roles[WAR.CurID]["我方"]~=WAR.roles[k]["我方"] and WAR.roles[k]["死亡"]==false then
						local dx=math.abs(i-WAR.roles[k]["坐标X"])
						local dy=math.abs(j-WAR.roles[k]["坐标Y"])
						if minDest>(dx+dy) then        --计算当前距离敌人最近的位置
							minDest=dx+dy
						end
					end
				end

				if minDest>maxDest then           --找一个最远的位置
					maxDest=minDest
					x=i
					y=j
				end
			end
		end
	end

	if maxDest>0 then
		War_MovePerson(x,y)    --移动到相应的位置
	end

end


---吃药
----flag=2 生命，3内力；4体力  6 解毒
function War_AutoEatDrug(flag)          ---吃药加参数
	local pid=WAR.roles[WAR.CurID]["人物编号"]
	local life=JY.roles[pid]["生命"]
	local maxlife=JY.roles[pid]["生命最大值"]
	local selectid
	local minvalue=math.huge

	local shouldadd
	local maxattrib
	local str
	if flag==2 then
		maxattrib=JY.roles[pid]["生命最大值"]
		shouldadd=maxattrib-JY.roles[pid]["生命"]
		str="加生命"
	elseif flag==3 then
		maxattrib=JY.roles[pid]["内力最大值"]
		shouldadd=maxattrib-JY.roles[pid]["内力"]
		str="加内力"
	elseif flag==4 then
		maxattrib=CC.PersonAttribMax["体力"]
		shouldadd=maxattrib-JY.roles[pid]["体力"]
		str="加体力"
	elseif flag==6 then
		maxattrib=CC.PersonAttribMax["中毒程度"]
		shouldadd=JY.roles[pid]["中毒程度"]
		str="加中毒解毒"
	else
		return 
	end

	local function Get_Add(thingid)     --定义物品增加的值
		if flag==6 then
			return -JY.items[thingid][str]/2   --解毒为负值
		else
			return JY.items[thingid][str]
		end
	end

	if WAR.roles[WAR.CurID]["我方"]==true then
		local extra=0
		for i =1, CC.MyThingNum do
			local thingid=JY.base["物品" ..i]
			if thingid>=0 then
				local add=Get_Add(thingid)
				if JY.items[thingid]["类型"]==3 and add>0 then
					local v=shouldadd-add
					if v<0 then               --可以加满, 用其他方法找合适药品
						extra=1
						break
					else
						if v<minvalue then        --寻找加生命后生命最大的
							minvalue=v
							selectid=thingid
						end
					end
				end
			end
		end
		if extra==1 then
			minvalue=math.huge
			for i =1, CC.MyThingNum do
				local thingid=JY.base["物品" ..i]
				if thingid>=0 then
					local add=Get_Add(thingid)
					if JY.items[thingid]["类型"]==3 and add>0 then
						local v=add-shouldadd
						if v>=0 then               --可以加满生命
							if v<minvalue then
								minvalue=v
								selectid=thingid
							end
						end
					end
				end
			end
		end
		if UseThingEffect(selectid,pid)==1 then       --使用有效果
			instruct_32(selectid,-1)            --物品数量减少
		end
	else
		local extra=0
		for i =1, 4 do
			local thingid=JY.roles[pid]["携带物品" ..i]
			if thingid>=0 then
				local add=Get_Add(thingid)
				if JY.items[thingid]["类型"]==3 and add>0 then
					local v=shouldadd-add
					if v<0 then               --可以加满生命, 用其他方法找合适药品
						extra=1
						break
					else
						if v<minvalue then        --寻找加生命后生命最大的
							minvalue=v
							selectid=thingid
						end
					end
				end
			end
		end
		if extra==1 then
			minvalue=math.huge
			for i =1, 4 do
				local thingid=JY.roles[pid]["携带物品" ..i]
				if thingid>=0 then
					local add=Get_Add(thingid)
					if JY.items[thingid]["类型"]==3 and add>0 then
						local v=add-shouldadd
						if v>=0 then               --可以加满生命
							if v<minvalue then
								minvalue=v
								selectid=thingid
							end
						end
					end
				end
			end
		end

		if UseThingEffect(selectid,pid)==1 then       --使用有效果
			instruct_41(pid,selectid,-1)            --物品数量减少
		end
	end

	lib.Delay(500)
end


